// Ancient Egyptian Broadcollar Necklace (Wesekh)
// Based on artifacts from New Kingdom period (1550-1077 BCE)
// Features realistic construction with tubular and disc beads

// Overall dimensions for 3D printing
collar_width = 90;
collar_height = 45;
bead_diameter = 3.2;
bead_height = 2.4;
thread_thickness = 1.0;

// Colors matching faience and gold
faience_blue = [0.2, 0.6, 0.9];
faience_green = [0.3, 0.7, 0.5];
gold = [0.8, 0.7, 0.2];

module tubular_bead(h, d, color_val) {
    color(color_val)
    cylinder(h=h, d=d, $fn=16);
}

module disc_bead(h, d, color_val) {
    color(color_val)
    cylinder(h=h, d=d, $fn=32);
}

module spacer_bead() {
    color(gold)
    difference() {
        cylinder(h=bead_height*1.5, d=bead_diameter*1.8, $fn=6);
        cylinder(h=bead_height*1.6, d=bead_diameter*0.8, $fn=16);
    }
}

module bead_row(count, start_y, bead_type) {
    row_width = count * bead_diameter * 1.1;
    for(i = [0:count-1]) {
        x_pos = (i - count/2 + 0.5) * bead_diameter * 1.1;
        translate([x_pos, start_y, 0])
            if(bead_type == "tubular")
                tubular_bead(bead_height*1.2, bead_diameter*0.9, faience_blue);
            else if(bead_type == "disc")
                disc_bead(bead_height*0.6, bead_diameter*1.1, faience_green);
    }
}

module terminal_piece() {
    // Falcon-head terminals characteristic of wesekh collars
    color(gold)
    union() {
        translate([0, -collar_height/2 - 8, 0])
            scale([1, 1.5, 0.8])
            sphere(d=8, $fn=32);

        // Falcon beak
        translate([0, -collar_height/2 - 12, 2])
            rotate([0, 30, 0])
            scale([0.3, 2, 0.3])
            cylinder(h=5, d1=2, d2=0.5, $fn=8);

        // Eye detail
        translate([-1.5, -collar_height/2 - 7, 3])
            color([0, 0, 0])
            sphere(d=1, $fn=8);
    }
}

// Main collar construction
union() {
    // Left half
    translate([-collar_width/4, 0, 0])
    mirror([1, 0, 0])
    union() {
        // Row construction from top to bottom
        bead_row(15, collar_height/2 - 4, "tubular");
        bead_row(17, collar_height/2 - 8, "disc");
        bead_row(19, collar_height/2 - 12, "tubular");
        bead_row(21, collar_height/2 - 16, "disc");
        bead_row(23, collar_height/2 - 20, "tubular");
        bead_row(25, collar_height/2 - 24, "disc");

        // Spacer beads at intervals
        for(i = [0:4]) {
            translate([0, collar_height/2 - 6 - i*8, 0])
                spacer_bead();
        }

        // Terminal piece
        translate([collar_width/2 - 5, 0, 0])
            terminal_piece();
    }

    // Right half (symmetrical)
    translate([collar_width/4, 0, 0])
    union() {
        bead_row(15, collar_height/2 - 4, "tubular");
        bead_row(17, collar_height/2 - 8, "disc");
        bead_row(19, collar_height/2 - 12, "tubular");
        bead_row(21, collar_height/2 - 16, "disc");
        bead_row(23, collar_height/2 - 20, "tubular");
        bead_row(25, collar_height/2 - 24, "disc");

        for(i = [0:4]) {
            translate([0, collar_height/2 - 6 - i*8, 0])
                spacer_bead();
        }

        translate([-collar_width/2 + 5, 0, 0])
            terminal_piece();
    }

    // Central counterweight (menat)
    translate([0, collar_height/2 + 5, -2])
    color(gold)
    union() {
        // Main counterweight plate
        scale([1.2, 1, 0.2])
        sphere(d=12, $fn=64);

        // Suspension loop
        translate([0, 6, 0])
            rotate([90, 0, 0])
            difference() {
                cylinder(h=3, d=6, $fn=32);
                cylinder(h=4, d=3, $fn=32);
            }
    }
}

// Medieval European tablet-woven braid fragment (13th14th century)
// A narrow band used as edging on garments, belts or ecclesiastical textiles
// Based on finds from London and York

$fa = 2;
$fs = 0.5;

// main module
module tablet_braid(){
    color("BurlyWood")
    union(){
        // warp threads  28 tablets  4 holes each = 112 warp ends
        for(i = [0:27]){                // 28 tablets across 50 mm
            rotate([0,0,i*12.8])        // twist angle between tablets
                tablet(i*1.8);          // 1.8 mm per tablet
        }

        // decorative weft border at one end (starting border)
        translate([0,-2,0])
            starting_border();

        // weft passes visible as ridges
        for(w = [-20:5:20]){
            translate([0,w,0])
                weft_ridge();
        }
    }
}

// one tablet with its four warp threads
module tablet(pos_x){
    // tablet disk (warp-beater)
    translate([pos_x,0,0])
    rotate([90,0,0])
    cylinder(h=1.2, d=10, $fn=32);

    // four warp threads per tablet
    for(a=[0:90:270]){
        rotate([0,0,a])
        translate([pos_x,0,0])
        warp_thread();
    }
}

// single wool warp thread (S-spun, Z-plied)
module warp_thread(){
    color("SaddleBrown")
    rotate([0,90,0])
    cylinder(h=50, d=1.2, center=true);
}

// weft ridge made by beaten weft thread
module weft_ridge(){
    color("Sienna")
    difference(){
        cube([50,1.8,0.8], center=true);
        // tiny gaps between ridges for printability
        for(i=[-23:4:23]){
            translate([i,0,0])
            cube([1.5,2,1], center=true);
        }
    }
}

// decorative starting border  typical chevron pattern
module starting_border(){
    color("DarkGoldenrod")
    union(){
        for(i=[-24:4:24]){
            translate([i,0,0])
            rotate([0,90,0])
            cylinder(h=2.5, d=3.5, $fn=6);
        }
    }
}

// call the model
tablet_braid();

// Anatolian tablet-weaving card
