        }

        // decorative raised rim on both faces
        translate([0, 0, 3])
        linear_extrude(height = 1)
        offset(delta = -2)
        circle(d = 60);

        translate([0, 0, -1])
        linear_extrude(height = 1)
        offset(delta = -2)
