// Historical Celtic Knotting Cross Artifact (8th-9th century AD)
// Realistic style with medium complexity - 3D printable model

// Overall dimensions: 60mm x 60mm x 8mm

// Base cross structure
module cross_base() {
    color("burlywood")
    union() {
        // Vertical beam
        translate([-4, -25, 0])
            cube([8, 50, 6]);

        // Horizontal beam
        translate([-25, -4, 0])
            cube([50, 8, 6]);

        // Center boss
        translate([-8, -8, 5])
            cylinder(h=3, r=8, $fn=32);
    }
}

// Celtic knot pattern - interlaced design
module knot_pattern() {
    color("goldenrod")
    for(i = [-1, 1]) {
        // Left and right knots
        translate([i*15, 0, 6.1]) {
            rotate([0, 0, 45])
