// Scarab beetle body
module scarab_body() {
    color(stone_color) {
        // Main body
        scale([1, 0.7, 0.5])
            sphere(d=scarab_length);

        // Head
        translate([scarab_length/2 - 2, 0, -scarab_height/4])
            sphere(d=4);

        // Wing cases
