patina = [80/255, 120/255, 90/255];

// Overall dimensions
total_length = 70;
bow_width = 25;
coil_diameter = 12;

module fibula() {
    color(bronze) {
        // Main bow with slight curve
        translate([0, 0, -5])
            rotate([0, -15, 0])
            union() {
                // Bow body - elongated oval shape
                hull() {
                    translate([-total_length/2 + 5, 0, 0])
                        sphere(d = bow_width - 5);
                    translate([total_length/2 - 5, 0, 0])
                        sphere(d = bow_width - 5);
                }

                // Ornamental ridge along bow
                translate([-total_length/2 + 10, 0, -2])
                    rotate([0, 0, 30])
                    cube([total_length - 20, 2, 4]);
            }

        // Catch plate at end
        translate([total_length/2 - 2, 0, -5])
            rotate([0, 0, -15])
            difference() {
                // Main catch plate
                cube([8, bow_width/2, 2]);

                // Slot for pin
                translate([4, -bow_width/6, -1])
                    cube([2, bow_width/3, 4]);
            }

        // Spring coil assembly
        translate([-total_length/2 + 3, 0, 0])
            rotate([90, 0, 0])
            union() {
                // Main coil (3 turns)
                for(i = [0:2]) {
                    translate([0, 0, i * coil_diameter/3])
                        rotate([0, 0, i * 120])
                        cylinder(h = 2, d1 = coil_diameter, d2 = coil_diameter - 0.5);
                }

                // Coil connecting bar
                translate([0, -coil_diameter/2, coil_diameter/2])
                    rotate([0, 90, 0])
                    cylinder(h = coil_diameter, d = 3);
            }

        // Pin arm
        translate([-total_length/2 + 3, -coil_diameter, coil_diameter/2])
            rotate([0, -90, 0])
            union() {
                // Pin shaft
                cylinder(h = total_length - 5, d = 2);

                // Pin point
                translate([0, 0, total_length - 5])
                    cylinder(h = 5, d1 = 2, d2 = 0.5);
            }

        // Decorative knobs
        for(pos = [-20, -5, 10]) {
            translate([pos, 0, -8])
                sphere(d = 4);
        }

        // Spiral decoration on bow
        for(i = [0:5]) {
            rotate([0, 0, i * 30])
            translate([i * 4 - 15, 0, -6])
                rotate([90, 0, 0])
                cylinder(h = 3, d1 = 2, d2 = 1);
        }
    }

    // Patina effects
    color(patina) {
        // Light patina on raised areas
        translate([0, 0, -6])
            scale([1.02, 1.02, 1.02])
            sphere(d = bow_width - 2);
    }
}

