        translate([0, 0, 0])
            sphere(symbol_size/4);
        translate([0, -symbol_size/2, 0])
            cylinder(h=symbol_size/2, r=symbol_size/6);
        translate([-symbol_size/3, -symbol_size, 0])
            rotate([0, 90, 0])
            cylinder(h=symbol_size/2, r=symbol_size/6);
        translate([symbol_size/3, -symbol_size, 0])
            rotate([0, 90, 0])
            cylinder(h=symbol_size/2, r=symbol_size/6);
    }
}

// Decorative border pattern
module border_pattern(length, height) {
    pattern_count = floor(length / 6);
    for(i = [0:pattern_count-1]) {
        translate([i * 6 + 3, 0, 0])
            color([0.9, 0.8, 0.5])
            cube([2, height, 1]);
    }
