    difference() {
        // Outer shell
        color([0.85, 0.75, 0.5])  // Sandstone color
        cube([box_width, box_depth, box_height]);

        // Inner cavity
        translate([wall_thickness, wall_thickness, wall_thickness])
            cube([box_width - 2*wall_thickness, 
                  box_depth - 2*wall_thickness, 
                  box_height - wall_thickness]);
    }
}

// Lid with curved top
module box_lid() {
    difference() {
        union() {
            // Base of lid
            color([0.85, 0.75, 0.5])
            translate([-1, -1, 0])
                cube([box_width + 2, box_depth + 2, wall_thickness]);

            // Curved top
            color([0.85, 0.75, 0.5])
            translate([box_width/2, box_depth/2, wall_thickness])
                scale([1, 0.6, 0.5])
                sphere(d=box_width * 0.9);
        }

        // Handle hole
        translate([box_width/2, box_depth/2, wall_thickness])
            cylinder(h=10, r=5);
    }
}

