    // Front hieroglyphs
    translate([box_width/2, box_depth, box_height/2])
        rotate([90, 0, 0])
        hieroglyph(8);

    // Side hieroglyphs
    translate([0, box_depth/2, box_height/2])
        rotate([0, 90, 90])
        hieroglyph(6);

    // Top border patterns
    translate([5, 0, box_height - 1])
        border_pattern(box_width - 10, 3);

    translate([5, box_depth, box_height - 1])
        border_pattern(box_width - 10, 3);

    // Side border patterns
    translate([0, 5, box_height - 1])
        rotate([0, 0, 90])
        border_pattern(box_depth - 10, 3);

    translate([box_width, 5, box_height - 1])
        rotate([0, 0, 90])
        border_pattern(box_depth - 10, 3);

    // Corner protectors
    for(x = [0, box_width]) {
        for(y = [0, box_depth]) {
            translate([x, y, box_height/2])
                color([0.7, 0.6, 0.3])
                cylinder(h=box_height, r=2);
        }
    }
