module spiral_groove(turns = 5, spacing = 3.5) {
    // Raised spiral ridge around disc
    for (i = [0:turns * 16]) {
        angle = i * 22.5;
        radius = 40 - (i * spacing);
        if (radius > 6) {
            translate([
                cos(angle) * radius,
                sin(angle) * radius,
                0
            ])
            color("wheat")
            cylinder(h = 1.5, d = 2.5, center = true);
        }
    }
}

module hieroglyph_symbol(type, angle, radius) {
    // Basic hieroglyphic symbols - simplified representations
    translate([
        cos(angle) * radius,
        sin(angle) * radius,
        3.5
    ])
    rotate([0, 0, angle + 90])
    color("saddlebrown")
    union() {
        if (type == 1) {
            // Walking man symbol
            cylinder(h = 1.5, d = 3);
            translate([0, -2, 0])
            cube([1, 3, 1.5], center = true);
        } else if (type == 2) {
