            for (j = [0:45:315]) {
                rotate([0, 0, j])
                translate([1.2, 0, 0])
                cylinder(h = 1.5, d = 1.5);
            }
        } else {
            // Fish symbol
            scale([1.5, 0.8, 1])
            sphere(d = 3);
        }
    }
}

