// Ancient Sumerian Clay Tablet with Cuneiform Inscriptions
// A realistic model of a cuneiform tablet with authentic proportions and features

$fn = 32;

// Overall dimensions
tablet_width = 60;
tablet_height = 40;
tablet_thickness = 15;

// Color for fired clay
color([0.6, 0.4, 0.3]) {

// Main tablet body - slightly curved for realism
difference() {
    union() {
        // Base tablet with gentle curvature
        translate([0, 0, tablet_thickness/2])
        scale([1, 1, 0.9])
        cube([tablet_width, tablet_height, tablet_thickness], center=true);

        // Rounded edges
        hull() {
            for(x = [-tablet_width/2+3, tablet_width/2-3])
                for(y = [-tablet_height/2+3, tablet_height/2-3])
                    translate([x, y, 0])
                    cylinder(r=3, h=tablet_thickness, center=true);
        }
    }

    // Carved center depression for writing surface
    translate([0, 0, tablet_thickness/2-0.5])
    cube([tablet_width-8, tablet_height-8, 2], center=true);
}

// Cuneiform inscriptions - wedge impressions
module cuneiform_wedge(x, y, angle, size) {
    translate([x, y, tablet_thickness/2+1])
    rotate([0, 0, angle])
    linear_extrude(height=1.5, convexity=10)
    polygon(points=[
        [-size, -size*0.3],
        [size, 0],
        [-size, size*0.3]
    ]);
}

// Rows of cuneiform text
for(row = [0:3]) {
    y_pos = -tablet_height/4 + row * 8;
    for(col = [0:4]) {
        x_pos = -tablet_width/3 + col * 10 + (row%2)*5;
        cuneiform_wedge(x_pos, y_pos, 45, 2);
        cuneiform_wedge(x_pos+3, y_pos, -45, 2);
    }
}

// Side columns with decorative pattern
module side_pattern() {
    for(i = [0:2]) {
        translate([0, -tablet_height/3 + i*10, 0])
        cube([1, 6, 0.5], center=true);
    }
}

// Left side decoration
translate([-tablet_width/2+2, 0, 0])
side_pattern();

// Right side decoration
translate([tablet_width/2-2, 0, 0])
side_pattern();

// Top and bottom decorative borders
module border_pattern(y_pos) {
    for(x = [-tablet_width/2+5:6:tablet_width/2-5]) {
        translate([x, y_pos, tablet_thickness/2+0.5])
        cylinder(r=1, h=1);
    }
}

border_pattern(tablet_height/2-3);
border_pattern(-tablet_height/2+3);

// Seal impression at bottom
translate([0, -tablet_height/4, tablet_thickness/2+1])
rotate([0, 0, 0])
cylinder(r=5, h=1, $fn=6);

// Small chip/damage for realism
translate([tablet_width/3, tablet_height/3, tablet_thickness/2])
sphere(r=2);

// Corner reinforcements (thicker clay at corners)
for(x = [-1, 1])
    for(y = [-1, 1]) {
        translate([x*tablet_width/2.2, y*tablet_height/2.2, 0])
