module cross() {
    // Gothic crucifix, 15 mm tall
    translate([0, 0, 40]) {
        difference() {
            union() {
                // Cross body
                cube([2, 8, 0.5], center=true);
                cube([8, 2, 0.5], center=true);
                // Fleur-de-lis style terminals
                for (a = [0:90:270]) {
                    rotate([0, 0, a])
                        translate([5, 0, 0])
                            scale([0.5, 0.5, 1])
                                cylinder(h=0.5, r1=2, r2=0);
                }
            }
            // Engraved IHS christogram
            translate([0, -1, 0.3])
                cube([2, 0.5, 0.2], center=true);
            translate([0, 0, 0.3])
                cube([1, 2, 0.2], center=true);
            translate([0, 1, 0.3])
                cube([2, 0.5, 0.2], center=true);
        }
    }
}

module beads() {
    // Rosary chain with Gothic spacing
    // Main loop: 5 decades (50 Hail Mary beads)
    for (i = [0:49]) {
        angle = i * 360 / 50;
        radius = 18;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            20
        ])
            sphere(d=2.5);
    }

    // Larger Our Father beads (5)
    for (i = [0:4]) {
        angle = i * 360 / 5;
        radius = 18;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            20
        ])
            sphere(d=3.5);
    }

    // Drop chain with 5 beads leading to cross
    for (i = [0:4]) {
        translate([0, 0, 35 - i * 3.5])
            sphere(d=i == 0 ? 3.5 : 2.5); // Larger bead at junction
    }

    // Period-correct connector rings between segments
    for (i = [0:4]) {
        angle = i * 72;
        radius = 18;
        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            20
        ])
            rotate([90, 0, angle])
                cylinder(h=1, r=0.8, center=true);
