// Medieval Psalter (Book of Psalms) - 12th Century Style
// Dimensions: 80mm x 60mm x 20mm closed

// Main book cover dimensions
cover_width = 80;
cover_height = 60;
cover_thickness = 2.5;
spine_width = 12;

// Page block dimensions
page_width = 75;
page_height = 55;
page_thickness = 15;

// Colors
wood_color = [139/255, 90/255, 43/255];
leather_color = [101/255, 67/255, 33/255];
gold_color = [212/255, 175/255, 55/255];
parchment_color = [240/255, 228/255, 196/255];

// Main cover module with wood core and leather binding
module book_cover() {
    // Wooden cover core
    color(wood_color)
    cube([cover_width, cover_height, cover_thickness], center=true);

    // Leather binding around edges
    color(leather_color) {
        difference() {
            cube([cover_width + 1, cover_height + 1, cover_thickness + 0.5], center=true);
            cube([cover_width - 1, cover_height - 1, cover_thickness + 1], center=true);
        }
    }

    // Metal corner reinforcements
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x * (cover_width/2 - 5), y * (cover_height/2 - 5), cover_thickness/2 + 0.1])
            color(gold_color)
            cylinder(h=0.5, r=3, $fn=6);
        }
    }

    // Central decorative boss
    translate([0, 0, cover_thickness/2 + 0.1])
    color(gold_color)
    cylinder(h=0.5, r=8, $fn=24);

    // Decorative cross on front
    translate([0, 0, cover_thickness/2 + 0.6])
    color(gold_color) {
        cube([2, 20, 0.5], center=true);
        cube([16, 2, 0.5], center=true);
    }
}

// Spine with raised bands
module spine() {
    color(leather_color) {
        cube([spine_width, cover_height, cover_thickness + page_thickness + 2], center=true);

        // Raised bands
        for(i = [-2, -1, 0, 1, 2]) {
            translate([0, i * 10, (cover_thickness + page_thickness + 2)/2 - 0.5])
            cube([spine_width - 1, 3, 1], center=true);
        }
    }
}

// Page block with gilded edges
module pages() {
    color(parchment_color)
    cube([page_width, page_height, page_thickness], center=true);

    // Gilded page edges
    color(gold_color) {
        // Top edge
        translate([0, page_height/2 + 0.1, 0])
        cube([page_width + 0.5, 0.2, page_thickness + 0.5], center=true);

        // Bottom edge
        translate([0, -page_height/2 - 0.1, 0])
        cube([page_width + 0.5, 0.2, page_thickness + 0.5], center=true);

        // Fore edge
        translate([page_width/2 + 0.1, 0, 0])
        cube([0.2, page_height + 0.5, page_thickness + 0.5], center=true);
    }
}

// Clasp and strap for closing
module clasp() {
    // Leather strap on back cover
    translate([cover_width/2 - 8, 0, cover_thickness/2 + 0.5])
    color(leather_color)
    cube([16, 4, 0.5]);

    // Metal clasp
    translate([cover_width/2 + 8, 0, cover_thickness/2 + 0.5])
    color(gold_color)
    difference() {
        cube([6, 8, 1]);
        translate([3, 0, -0.1])
        cylinder(h=2, r=2, $fn=16);
    }
}

// Complete psalter assembly
union() {
    // Back cover
    translate([0, 0, -page_thickness/2 - cover_thickness/2 - 1])
    book_cover();

    // Front cover
    translate([0, 0, page_thickness/2 + cover_thickness/2 + 1])
    rotate([0, 180, 0])
    book_cover();

    // Spine
    translate([-(cover_width + spine_width)/2 - 0.5, 0, 0])
    spine();

    // Pages
    pages();

    // Clasp assembly on back cover
    translate([0, 0, -page_thickness/2 - cover_thickness/2 - 1])
    clasp();
}

