// Main dimensions (scaled for 3D printing)
length = 75;
width = 18;
depth = 12;
wall_thickness = 2;

// Colors
brass_color = [181/255, 166/255, 66/255];
dark_brass_color = [150/255, 130/255, 50/255];
patina_color = [0.3, 0.4, 0.3];

// Main body with historical proportions
module mezuzah_body() {
    difference() {
        // Outer case
        color(brass_color)
        cube([length, width, depth], center = true);

        // Inner cavity for scroll
        translate([0, 0, -wall_thickness/2])
        cube([length - wall_thickness*2, width - wall_thickness*2, depth - wall_thickness], center = true);

        // Top opening
        translate([0, 0, depth/2])
        cube([length - 20, width - 4, wall_thickness + 0.1], center = true);
    }
}

// Medieval decorative end caps
module end_caps() {
    // Left cap with Star of David motif
    translate([-length/2 - 3, 0, 0])
    color(dark_brass_color) {
        cylinder(h = width, r = width/2, center = true);
        rotate([90, 0, 0])
        cylinder(h = width, r = width/2 - 2, center = true);
    }

    // Right cap with traditional Hebrew letter Shin
    translate([length/2 + 3, 0, 0])
    color(dark_brass_color) {
        cylinder(h = width, r = width/2, center = true);

        // Hebrew letter Shin (simplified)
        for(i = [-1, 0, 1]) {
            translate([0, i * 3, 0])
            cube([2, 1, width - 2], center = true);
        }
        for(i = [-1, 1]) {
            rotate([0, 90, 30 * i])
            translate([0, 0, 0])
            cube([1.5, 4, 1.5], center = true);
        }
    }
}

// Historical decorative border pattern
module decorative_border() {
    color(brass_color)
    for(i = [0:10:length-10]) {
        translate([i - length/2 + 5, 0, depth/2 + 0.1])
        scale([1, 0.8, 1])
        rotate([90, 0, 0])
        cylinder(h = 2, r1 = 2, r2 = 1);
    }
}

// Medieval geometric patterns
module geometric_patterns() {
    color(dark_brass_color)
    // Diamond pattern on front face
    for(x = [-length/2 + 8:10:length/2 - 8]) {
        for(y = [-width/2 + 4:6:width/2 - 4]) {
            translate([x, y, depth/2 + 0.2])
            rotate([0, 0, 45])
            cube([4, 4, 0.5], center = true);
        }
    }
}

// Patina/age effects
module age_effects() {
    color(patina_color)
    for(i = [1:15]) {
        translate([
            length * (0.1 + 0.8 * rands(0, 1, 1)[0]) - length/2,
            width * (0.1 + 0.8 * rands(0, 1, 1)[0]) - width/2,
            depth/2 + 0.3
        ])
        sphere(r = rands(0.5, 1.5, 1)[0]);
    }
}

// Assembly
union() {
    mezuzah_body();
    end_caps();
    decorative_border();
    geometric_patterns();
    age_effects();
}

