// Colors
wood_color = "#8B4513";
leather_color = "#4A2C17";
gold_color = "#FFD700";
page_color = "#F5F5DC";
metal_color = "#C0C0C0";

// Wooden cover with raised border
module wooden_cover() {
    color(wood_color) {
        // Main cover board
        translate([0, 0, 0])
            cube([book_width, book_height, cover_thickness], center=true);

        // Raised border
        translate([0, 0, cover_thickness/2 + 0.5])
            cube([book_width - 4, book_height - 4, 1], center=true);
    }
}

// Leather spine with raised bands
module leather_spine() {
    color(leather_color) {
        // Main spine
        translate([book_width/2 + 0.5, 0, page_thickness/2 + cover_thickness/2])
            cube([2, book_height, page_thickness + cover_thickness], center=true);

        // Raised bands (5 cords)
        for (i = [-2:2]) {
            translate([book_width/2 + 0.5, i * 12, page_thickness/2 + cover_thickness/2 + 1])
                cube([2.5, 3, page_thickness + cover_thickness + 2], center=true);
        }
    }
}

// Metal corner protectors
module corner_protectors() {
    color(metal_color) {
        // Define corner positions
        positions = [
            [-book_width/2 + 3, book_height/2 - 3],
            [book_width/2 - 3, book_height/2 - 3],
            [-book_width/2 + 3, -book_height/2 + 3],
            [book_width/2 - 3, -book_height/2 + 3]
        ];

        for (pos = positions) {
            translate([pos[0], pos[1], cover_thickness/2 + 0.5]) {
                // Corner triangle
                linear_extrude(height=metal_thickness)
                    polygon(points=[[0,0], [5,0], [0,5]]);

                // Decorative rivet
                translate([1, 1, metal_thickness/2])
                    cylinder(h=2, r=0.5);
            }
        }
    }
}

// Decorative clasp and lock
module clasp() {
    color(gold_color) {
        // Main clasp on front cover
        translate([book_width/2 - 8, 0, cover_thickness + 1])
            cube([6, 10, 2]);

        // Hinge part
        for (i = [-1, 1]) {
            translate([book_width/2 - 8, i * 4, cover_thickness + 2])
                cylinder(h=3, r=1);
        }

        // Lock plate on spine
        translate([book_width/2 + 1, 0, page_thickness/2 + cover_thickness/2])
            cube([2, 8, 3], center=true);
    }
}

// Pages visible at edges
module pages() {
    color(page_color) {
        translate([0, 0, 0])
            cube([book_width - 1, book_height - 1, page_thickness], center=true);

        // Page edges detail
        for (i = [-4:4]) {
            translate([book_width/2 - 1, i * 8, 0])
                cube([0.5, 6, page_thickness], center=true);
        }
    }
}

// Central boss decoration
module boss_decoration() {
    color(gold_color) {
        translate([0, 0, cover_thickness + 1]) {
            cylinder(h=2, r=8);
            cylinder(h=3, r=4);
            cylinder(h=4, r=2);
        }
    }
}

// Assemble the manuscript
union() {
    // Bottom cover
    translate([0, 0, -page_thickness/2 - cover_thickness/2])
        wooden_cover();

    // Top cover
    translate([0, 0, page_thickness/2 + cover_thickness/2])
        wooden_cover();

    // Pages between covers
    pages();

    // Spine
    leather_spine();

    // Metal protectors
    corner_protectors();

    // Clasp and lock mechanism
    clasp();

    // Central decoration on front cover
    translate([0, 0, page_thickness/2 + cover_thickness])
        boss_decoration();
}

// Medieval illuminated codex, 9th century style
// 3D-printable model  medium complexity

$fn = 32;
cover_t = 3;        // thickness of wooden boards
page_t = 0.8;       // thickness of one folio block
metal_t = 1;        // thickness of corner bosses
boss_r = 4;         // radius of corner boss
strap_w = 6;        // width of leather strap
strap_t = 1.2;
spine_h = 8;        // raised spine height

// Main assembly
color("saddlebrown")          // leather cover
difference() {
    codex_body();
    codex_details();
}
color("darkgoldenrod") corner_bosses();   // brass fittings
color("tan") spine_straps();

module codex_body() {
    // wooden core covered with leather
    difference() {
        union() {
            // front cover
            translate([0, 0, cover_t/2])
                cube([70, 95, cover_t], center=true);
            // back cover (mirror of front)
            mirror([0, 0, 1])
                translate([0, 0, cover_t/2])
                    cube([70, 95, cover_t], center=true);
            // spine
            translate([-70/2 - cover_t/2, 0, 0])
                cube([cover_t, 95, spine_h*2 + cover_t*2 + page_t*2], center=true);
        }

        // hollow out for pages
        difference() {
            translate([0, 0, 0])
                cube([65, 90, page_t*2 + 2], center=true);
            // page gap
            translate([0, 0, 0])
                cube([63, 88, page_t*2 + 4], center=true);
        }
    }
}

module codex_details() {
    // central cross motif
    translate([0, 0, cover_t/2 + 0.2])
        union() {
            cube([8, 40, 0.5], center=true);
            cube([40, 8, 0.5], center=true);
        }
    // four corner circles
    for(x = [-1, 1])
        for(y = [-1, 1])
            translate([x*25, y*35, cover_t/2 + 0.2])
                cylinder(h=0.5, r=6);
}

module corner_bosses() {
    // brass protective corners
    for(x = [-1, 1])
        for(y = [-1, 1])
            translate([x*32, y*42, cover_t/2 + metal_t/2])
                difference() {
                    cylinder(h=metal_t, r=boss_r);
                    cylinder(h=metal_t+0.1, r=boss_r-1);
                }
    // same on back
    mirror([0, 0, 1])
        for(x = [-1, 1])
            for(y = [-1, 1])
                translate([x*32, y*42, cover_t/2 + metal_t/2])
                    difference() {
                        cylinder(h=metal_t, r=boss_r);
                        cylinder(h=metal_t+0.1, r=boss_r-1);
                    }
}

module spine_straps() {
    // raised leather straps over spine
    for(i = [-2:1:2])
        translate([-70/2 - cover_t/2, i*18, 0])
            rotate([0, 90, 0])
                cylinder(h=strap_w, r=strap_t, center=true);
}

// Ancient Egyptian Scarab Amulet
// Based on New Kingdom scarabs (1550-1077 BCE)

$fn = 32;

// Main colors
color_bronze = [0.72, 0.45, 0.2];
color_dark_bronze = [0.55, 0.35, 0.15];
color_stone = [0.85, 0.8, 0.7];

// Overall dimensions
scarab_width = 35;
scarab_length = 45;
scarab_height = 18;

module scarab_body() {
    difference() {
        // Main body - oval shape
        color(color_stone)
        scale([1, 1.2, 0.6])
        sphere(d = scarab_width);

        // Flatten bottom
        translate([0, 0, -scarab_height/2])
        cube([scarab_width*2, scarab_length*2, scarab_height], center = true);
    }
}

module wing_cases() {
    // Left wing case
    translate([-scarab_width/4, 0, scarab_height/4])
    rotate([0, 15, -15])
    color(color_dark_bronze)
    scale([1.2, 0.3, 0.8])
    cylinder(h = scarab_length/2, d1 = scarab_width/3, d2 = scarab_width/4);

    // Right wing case
    translate([scarab_width/4, 0, scarab_height/4])
    rotate([0, 15, 15])
    color(color_dark_bronze)
    scale([1.2, 0.3, 0.8])
    cylinder(h = scarab_length/2, d1 = scarab_width/3, d2 = scarab_width/4);
}

module head() {
    // Scarab head
    translate([0, scarab_length/2 - 5, scarab_height/3])
    color(color_stone)
    scale([0.5, 0.4, 0.5])
    sphere(d = scarab_width/2);

    // Eyes
    for(x = [-scarab_width/6, scarab_width/6]) {
        translate([x, scarab_length/2 - 3, scarab_height/2.5])
        color(color_dark_bronze)
        sphere(d = 2);
    }
}

module legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Front pair
        translate([side * scarab_width/3, scarab_length/3, 0])
        rotate([0, 90, side * -30])
        color(color_bronze)
        cylinder(h = 8, d = 2);

        // Middle pair
        translate([side * scarab_width/2.5, 0, -2])
        rotate([0, 90, side * -20])
        color(color_bronze)
        cylinder(h = 10, d = 2);

        // Back pair
        translate([side * scarab_width/3, -scarab_length/3, 0])
        rotate([0, 90, side * -10])
        color(color_bronze)
        cylinder(h = 8, d = 2);
    }
}

module hieroglyphs_base() {
    // Base with hieroglyph inscription
    translate([0, 0, -scarab_height/2 - 1])
    color(color_bronze)
    difference() {
        cube([scarab_width + 8, scarab_length + 8, 2], center = true);

        // Hieroglyph pattern - simplified lotus and sun symbols
        for(i = [-2:2]) {
            translate([i * 6, 0, -0.5])
            cylinder(h = 3, d = 3);
        }
        for(i = [-1:1]) {
            translate([i * 8, 8, -0.5])
            cylinder(h = 3, d = 3);
            translate([i * 8, -8, -0.5])
            cylinder(h = 3, d = 3);
        }
    }
}

module decorative_elements() {
    // Wing patterns
    for(angle = [0:60:300]) {
        rotate([0, 0, angle])
        translate([0, 0, scarab_height/3])
        color(color_bronze)
        scale([0.8, 0.1, 0.05])
        rotate([90, 0, 0])
        cylinder(h = scarab_width/2.5, d = 8);
    }

    // Central line on back
    translate([0, 0, scarab_height/2.5])
    color(color_dark_bronze)
    scale([0.1, 1.2, 0.1])
    sphere(d = scarab_width);
}

// Assemble the scarab
union() {
    scarab_body();
    wing_cases();
    head();
    legs();
    hieroglyphs_base();
    decorative_elements();
}

// Triptych Historical Artifact - Medieval Gothic Altarpiece
// Dimensions: 80mm wide x 60mm tall x 15mm deep when closed

// Overall scale factor
scale_factor = 1;

// Panel dimensions
panel_width = 25 * scale_factor;
panel_height = 60 * scale_factor;
panel_depth = 3 * scale_factor;
hinge_width = 2.5 * scale_factor;

module triptych_frame() {
    // Left panel frame
    color("burlywood") {
        difference() {
            // Outer frame
            translate([-40, 0, 0]) cube([panel_width, panel_height, panel_depth]);
            // Inner cutout
            translate([-38, 2, -0.5]) cube([panel_width - 4, panel_height - 4, panel_depth + 1]);
        }

        // Right panel frame (mirrored)
        mirror([1, 0, 0]) 
            difference() {
                translate([-40, 0, 0]) cube([panel_width, panel_height, panel_depth]);
                translate([-38, 2, -0.5]) cube([panel_width - 4, panel_height - 4, panel_depth + 1]);
            }

        // Center panel frame
        translate([0, 0, 0]) {
            difference() {
                cube([panel_width, panel_height, panel_depth]);
                translate([2, 2, -0.5]) cube([panel_width - 4, panel_height - 4, panel_depth + 1]);
            }
        }
    }
}

module gothic_arch() {
    // Gothic arch decoration for top of panels
    color("goldenrod") {
        for(i = [-1, 0, 1]) {
            translate([i * 27.5, 58, panel_depth]) {
                rotate([90, 0, 0]) cylinder(h=2, r1=8, r2=8, $fn=50);
                translate([0, 0, 2]) rotate([90, 0, 0]) cylinder(h=8, r1=8, r2=0, $fn=3);
            }
        }
    }
}

module decorative_rosettes() {
    // Rosette decorations on frame corners
    color("gold") {
        for(x = [-37.5, -12.5, 12.5, 37.5]) {
            for(y = [5, 55]) {
                translate([x, y, panel_depth + 0.5]) {
                    cylinder(h=0.5, r=3, $fn=16);
                    for(i = [0:45:315]) {
                        rotate([0, 0, i]) 
                            translate([2, 0, 0]) 
                                cube([1, 0.5, 0.5], center=true);
                    }
                }
            }
        }
    }
}

module central_reliefs() {
    // Central religious scene reliefs
    color("wheat") {
        // Central figure (simplified Madonna and Child)
        translate([12.5, 30, -2]) {
            // Madonna figure
            sphere(r=3, $fn=20);
            translate([0, -4, 0]) sphere(r=2, $fn=20);
            // Halo
            color("gold") 
                translate([0, 0, 3]) rotate([90, 0, 0]) 
                    cylinder(h=0.5, r1=4, r2=4, $fn=50);
        }
    }
}

module side_panels_reliefs() {
    // Saint figures on side panels
    color("wheat") {
        // Left panel saint
        translate([-27.5, 30, -2]) {
            sphere(r=2.5, $fn=20);
            translate([0, -3, 0]) sphere(r=1.5, $fn=20);
        }

        // Right panel saint (mirrored)
        translate([27.5, 30, -2]) {
            sphere(r=2.5, $fn=20);
            translate([0, -3, 0]) sphere(r=1.5, $fn=20);
        }
    }
}

module inscription_band() {
    // Latin inscription band on center panel
    color("darkgoldenrod") {
        translate([12.5, 10, panel_depth - 1]) {
            cube([20, 2, 0.5], center=true);
            // Simplified text as raised dots
            for(i = [-9:2:9]) {
                translate([i, 0, 0.25]) sphere(r=0.3, $fn=8);
            }
        }
    }
}

module hinges() {
    // Simple hinge mechanisms
    color("darkgray") {
        for(i = [0:1]) {
            translate([i * 27.5 - 13.75, 5, panel_depth/2]) {
                rotate([90, 0, 0]) cylinder(h=panel_depth, r=1, $fn=12);
            }
            translate([i * 27.5 - 13.75, 55, panel_depth/2]) {
                rotate([90, 0, 0]) cylinder(h=panel_depth, r=1, $fn=12);
            }
        }
    }
}

// Main assembly
union() {
    triptych_frame();
    gothic_arch();
    decorative_rosettes();
    central_reliefs();
    side_panels_reliefs();
    inscription_band();
    hinges();
}

