// Roman ivory diptych panel (consular diptych style)
// Based on 4th-5th century AD examples
// Overall dimensions: 90mm x 60mm x 8mm when closed

$fn = 64;

// Main panel dimensions
panel_w = 45;
panel_h = 60;
panel_d = 3;
hinge_d = 4;
border_w = 3;

// Colors
ivory = [1, 0.95, 0.85];
bronze = [0.7, 0.4, 0.2];

module panel() {
    difference() {
        // Main panel body
        color(ivory)
        cube([panel_w, panel_h, panel_d], center=true);

        // Inner recess
        translate([0, 0, panel_d/2 - 0.5])
            cube([panel_w - 2*border_w, panel_h - 2*border_w, 1], center=true);
    }

    // Decorative border pattern
    for (i = [-1, 1]) {
        for (j = [-1, 1]) {
            translate([i*(panel_w/2 - border_w/2), j*(panel_h/2 - border_w/2), panel_d/2])
                cube([border_w, border_w, 0.5], center=true);
        }
    }

    // Corner rosettes
    module rosette(x, y) {
        translate([x, y, panel_d/2 + 0.2])
            for (a = [0:45:315]) {
                rotate([0, 0, a])
                    translate([border_w/2, 0, 0])
                        cylinder(r1=1.5, r2=0.5, h=0.3, $fn=6);
            }
    }

    rosette(-(panel_w/2 - border_w), -(panel_h/2 - border_w));
    rosette(panel_w/2 - border_w, -(panel_h/2 - border_w));
    rosette(-(panel_w/2 - border_w), panel_h/2 - border_w);
    rosette(panel_w/2 - border_w, panel_h/2 - border_w);
}

// Central portrait medallion
module medallion() {
    translate([0, 8, panel_d/2 + 0.1])
        difference() {
            // Outer ring
            color(bronze)
            cylinder(r=10, h=1, center=true);
            cylinder(r=8, h=2, center=true);

            // Inner ivory disk
            color(ivory)
            translate([0, 0, -0.1])
                cylinder(r=7.5, h=1.2, center=true);
        }

    // Simplified facial features
    translate([0, 8, panel_d/2 + 0.8]) {
        // Eyes
        color([0.1, 0.1, 0.1])
        translate([-2.5, 1, 0])
            sphere(r=0.8);
        translate([2.5, 1, 0])
            sphere(r=0.8);

        // Nose
        translate([0, -1, 0])
            cube([1, 2, 0.5], center=true);

        // Mouth
        translate([0, -3, 0])
            cube([3, 0.5, 0.3], center=true);
    }
}

// Latin inscription panel
module inscription() {
    translate([0, -12, panel_d/2 + 0.1])
        color([0.3, 0.3, 0.3])
        cube([20, 6, 0.2], center=true);
}

// Hinge mechanism
module hinge() {
    // Left panel hinge
    translate([panel_w/2, 0, 0])
        rotate([90, 0, 0])
            cylinder(r=hinge_d/2, h=panel_h/4, center=true);

    // Right panel hinge
    translate([-panel_w/2, 0, 0])
        rotate([90, 0, 0])
            cylinder(r=hinge_d/2, h=panel_h/4, center=true);
}

// Assemble left panel
module left_panel() {
    panel();
    medallion();
    inscription();
    hinge();
}

// Assemble right panel (mirror of left)
module right_panel() {
    mirror([1, 0, 0])
        left_panel();
}

// Main assembly
translate([-panel_w/2 - 1, 0, 0])
    left_panel();

translate([panel_w/2 + 1, 0, 0])
    right_panel();

// Gothic Retable - Medium Complexity Historical Artifact
// Based on 15th century Spanish retable altarpiece
// Designed for 3D printing - approximately 80mm wide x 60mm tall x 25mm deep

$fn = 32;

// Main colors
color("burlywood") {
    // Base platform
    translate([0, -5, 0]) {
        cube([80, 10, 5], center = true);
    }

    // Main altar structure
    difference() {
        translate([0, 0, 30]) {
            cube([70, 20, 60], center = true);
        }
        // Inner cavity for depth
        translate([0, 5, 35]) {
            cube([64, 10, 50], center = true);
        }
    }
}

// Gothic arch frame
module gothic_arch(width, height, depth) {
    difference() {
        // Main arch shape
        translate([0, 0, height/2]) {
            cube([width, depth, height], center = true);
        }
        // Gothic arch cutout
        union() {
            translate([0, -depth/2 - 1, height*0.7]) {
                cylinder(h = depth + 2, r = width/2 - 2, center = true);
            }
            translate([0, -depth/2 - 1, height*0.3]) {
                cube([width - 4, depth + 2, height*0.4], center = true);
            }
        }
    }
}

// Central gothic arch
color("goldenrod") {
    translate([0, 0, 55]) {
        gothic_arch(30, 35, 4);
    }
}

// Side arches
for (x = [-25, 25]) {
    color("goldenrod") {
        translate([x, 0, 45]) {
            scale([0.7, 1, 0.8]) {
                gothic_arch(25, 30, 4);
            }
        }
    }
}

// Decorative finials
module finial() {
    color("gold") {
        translate([0, 0, 0]) {
            cylinder(h = 8, r1 = 2, r2 = 1);
            translate([0, 0, 8]) {
                sphere(r = 2);
            }
        }
    }
}

// Top finials
