// Medieval Reredos - Decorative Altar Screen
// Based on 14th century English church architecture
// Overall dimensions: 80mm wide x 60mm tall x 15mm deep

// Main color for stone appearance
color([0.8, 0.8, 0.75]) {

    // Base altar platform
    translate([0, 0, 0]) {
        cube([80, 15, 8], center = true);
    }

    // Central arch
    translate([0, 0, 8]) {
        difference() {
            cube([45, 12, 50], center = true);
            translate([0, 0, 20]) {
                cylinder(h = 25, r1 = 22, r2 = 22, center = true);
            }
            translate([0, -6, 0]) {
                cube([43, 12, 48], center = true);
            }
        }
    }

    // Left wing
    translate([-27, 0, 8]) {
        difference() {
            cube([20, 12, 45], center = true);
            translate([0, 0, 17.5]) {
                cylinder(h = 20, r1 = 10, r2 = 10, center = true);
            }
            translate([0, -6, 0]) {
                cube([18, 12, 43], center = true);
            }
        }
    }

    // Right wing
    translate([27, 0, 8]) {
        mirror([1, 0, 0]) {
            children();
        }
    }

    // Decorative finials on top of arches
    module finial(x_pos) {
        translate([x_pos, 0, 58]) {
            cylinder(h = 8, r1 = 4, r2 = 2, center = true);
            translate([0, 0, 4]) {
                sphere(r = 2);
            }
        }
    }

    // Add three finials
    finial(0);
    finial(-27);
