// Egyptian Broad Collar (Wesekh) - Stole-style historical artifact
// Based on New Kingdom period examples, 3D printable at ~80mm width

$fn = 32;

// Main dimensions
width = 80;
height = 40;
thickness = 4;

// Color definitions
gold = "#D4AF37";
lapis = "#191970";
carnelian = "#B22222";
turquoise = "#40E0D0";

// Main collar band
module collar_band() {
    color(gold)
    difference() {
        // Outer shape - tapered towards ends
        hull() {
            translate([-width/2, 0, 0]) cube([width*0.1, height, thickness], center=true);
            translate([width/2, 0, 0]) cube([width*0.1, height, thickness], center=true);
            translate([0, height/2 - 2, 0]) cube([width*0.8, height*0.2, thickness], center=true);
        }

        // Inner cutout
        translate([0, 0, -1]) 
            cube([width*0.6, height*0.6, thickness+2], center=true);
    }
}

// Decorative bead row
module bead_row(y_pos, bead_count, bead_color) {
    color(bead_color)
    for(i = [0:bead_count-1]) {
        x_pos = -width/2 + width/(bead_count-1)*i + 5;
        translate([x_pos, y_pos, thickness/2]) 
            sphere(d=2.5);
    }
}

// Lotus flower motif
module lotus_motif(x_pos, y_pos) {
    translate([x_pos, y_pos, thickness/2 + 0.5]) {
        // Petals
        for(angle = [0:60:300]) {
            rotate([0, 0, angle]) 
                translate([0, 3, 0]) 
                    scale([1, 1.5, 0.5])
                        sphere(d=3);
        }
        // Center
        color(gold) sphere(d=2);
    }
}

// Falcon terminals
module falcon_terminal(side) {
    mirror([side, 0, 0]) {
        translate([width/2 - 5, 0, 0]) {
            // Falcon head
            color(gold) {
                rotate([0, 90, 0]) 
                    cylinder(h=4, d1=6, d2=4);
                // Beak
                translate([2, 0, 0]) 
                    rotate([0, -30, 0]) 
                        cylinder(h=3, d1=2, d2=1);
            }

            // Eye detail
            color(lapis) 
                translate([0, 2, 1]) 
                    sphere(d=1);
        }
    }
}

// Hieroglyphic inscription band
module inscription_band() {
    color(lapis)
    translate([0, -height/3, thickness/2 + 0.1]) {
        for(i = [0:7]) {
            x_pos = -width/2 + 10 + i*8;
            // Simplified hieroglyphs as cylinders
            rotate([0, 0, i*15]) 
                translate([x_pos, 0, 0]) 
                    cylinder(h=0.5, d=3);
        }
    }
}

// Assembly
union() {
    // Main collar structure
    collar_band();

    // Decorative elements
    bead_row(10, 12, lapis);
    bead_row(5, 10, carnelian);
    bead_row(0, 14, turquoise);
    bead_row(-5, 12, lapis);

    // Lotus motifs
    lotus_motif(-width/4, height/3);
    lotus_motif(width/4, height/3);
    lotus_motif(0, height/2 - 2);

    // Falcon terminals
    falcon_terminal(1);
    falcon_terminal(-1);

    // Hieroglyphic band
    inscription_band();
}

