// Module for clavi (vertical purple stripes)
module clavi(stripe_width) {
    color([0.4, 0.1, 0.6])  // Imperial purple
    translate([0, 0, -alb_height/2 + 5])
        cube([stripe_width, alb_width, alb_height - 10], center=true);
