// Module for decorative cuff
module cuff_decoration(radius) {
    color([0.8, 0.7, 0.3])  // Gold thread
    for(i = [0:45:315]) {
        rotate([0, 0, i])
        translate([radius, 0, 0])
            cylinder(h=1, r1=1, r2=0.5, $fn=6);
    }
}

// Module for embroidered hem border
module hem_border() {
    color([0.8, 0.7, 0.3])  // Gold thread
    difference() {
        // Main border band
        translate([0, 0, -alb_height/2 + hem_thickness/2])
            cube([alb_width + 4, alb_width + 4, hem_thickness], center=true);

        // Cut out center for hollow border
        translate([0, 0, -alb_height/2 + hem_thickness/2 - 0.1])
            cube([alb_width - 2, alb_width - 2, hem_thickness + 0.2], center=true);
    }

    // Decorative squares pattern
    for(x = [-10:5:10]) {
        for(y = [-10:5:10]) {
            if(abs(x) + abs(y) > 8) {
                color([0.6, 0.2, 0.8])
                translate([x, y, -alb_height/2 + hem_thickness])
                    cube([1.5, 1.5, 0.5], center=true);
            }
        }
    }
}

// Main alb body
module alb_body() {
    color([0.95, 0.95, 0.95])  // Off-white linen

    // Torso part
    union() {
        translate([0, 0, 0])
            cube([alb_width, alb_width/2, alb_height], center=true);

        // Sleeves
        translate([alb_width/2 + sleeve_length/2, 0, alb_height/4])
            rotate([0, 90, 0])
                cylinder(h=sleeve_length, r=alb_width/4, center=true);

        translate([-alb_width/2 - sleeve_length/2, 0, alb_height/4])
            rotate([0, -90, 0])
                cylinder(h=sleeve_length, r=alb_width/4, center=true);

        // Neck opening (subtractive)
        translate([0, 0, alb_height/2 - 8])
            rotate([90, 0, 0])
                cylinder(h=20, r1=4, r2=6, center=true, $fn=32);
    }
}

// Assembly
