    // Main garment
    alb_body();

    // Decorative elements
    clavi(2);
    translate([alb_width/2 + sleeve_length - 2, 0, alb_height/4])
        cuff_decoration(alb_width/5);
    translate([-alb_width/2 - sleeve_length + 2, 0, alb_height/4])
        cuff_decoration(alb_width/5);

    // Hem decoration
    hem_border();

    // Small cross embroidery on chest
    color([0.8, 0.7, 0.3])
    translate([0, 0, alb_height/2 - 10])
    union() {
        cube([1, 8, 1], center=true);
        cube([8, 1, 1], center=true);
    }
}

