// Chisel - Bronze Age style woodworking chisel
// Medium complexity historical artifact
// Features socketed design with decorative rings and weathering details

$fn = 32;

// Main dimensions
chisel_length = 85;
blade_length = 35;
blade_width = 12;
blade_thickness = 4;
socket_outer = 10;
socket_inner = 7;
socket_length = 25;

// Colors
color_chisel = [150/255, 120/255, 100/255]; // Bronze
color_dark = [100/255, 80/255, 60/255];     // Weathered bronze

// Create the complete chisel
module chisel() {
    union() {
        // Blade
        translate([0, 0, chisel_length/2])
            rotate([90, 0, 0])
                blade();

        // Socket
        translate([0, 0, chisel_length - socket_length/2])
            rotate([90, 0, 0])
                socket();

        // Decorative rings on socket
        for(i = [1:3]) {
            translate([0, 0, chisel_length - socket_length/2 + i*6])
                rotate([90, 0, 0])
                    decorative_ring();
        }
    }
}

// Blade with tapered cutting edge
module blade() {
    color(color_chisel)
    difference() {
        // Main blade body
        union() {
            // Blade shaft
            cube([blade_thickness, blade_width, blade_length], center=true);

            // Tapered cutting edge
            translate([0, 0, blade_length/2 + 2])
                scale([1, 1, 0.5])
                    cube([blade_thickness, blade_width, 8], center=true);
        }

        // Create beveled cutting edge
        translate([0, blade_width/2, blade_length/2 + 2])
            rotate([0, 45, 0])
                cube([blade_thickness*2, blade_width, blade_thickness*2]);

        // Opposite bevel
        translate([0, -blade_width/2, blade_length/2 + 2])
            rotate([0, -45, 0])
                cube([blade_thickness*2, blade_width, blade_thickness*2]);

        // Weathering grooves
        for(i = [0:2]) {
            translate([0, blade_width/2 - (i+1)*3, -blade_length/2 + i*8])
                rotate([0, 0, 45])
                    cube([blade_thickness+0.5, 0.5, 8]);
        }
    }
}

// Socket for handle attachment
module socket() {
    color(color_chisel)
    difference() {
        // Outer socket
        cylinder(h=socket_length, r=socket_outer, center=true);

        // Inner hole for handle
        cylinder(h=socket_length+2, r=socket_inner, center=true);

        // Decorative notches
        for(i = [0:5]) {
            rotate([0, 0, i*60])
                translate([socket_outer-1, 0, socket_length/2 - 3])
                    cube([2, socket_outer/2, 6]);
        }
    }
}

