$fn = 32;

module saw_tooth() {
    difference() {
        cube([tooth_pitch, blade_width, tooth_height]);
        translate([0, 0, tooth_height])
            rotate([0, 45, 0])
                cube([tooth_pitch*2, blade_width*2, tooth_height]);
    }
}

module saw_teeth() {
    teeth_count = floor(blade_length / tooth_pitch);
    for(i = [0:teeth_count-1]) {
        translate([i*tooth_pitch + tooth_pitch/2, 0, 0])
            saw_tooth();
    }
}

module blade() {
    // Main blade body
    color("bronze")
    difference() {
        union() {
            // Tapered blade
            hull() {
                translate([0, 0, 0])
                    cube([blade_length*0.3, blade_width, blade_thickness]);
                translate([blade_length*0.7, 0, 0])
                    cube([blade_length*0.3, blade_width*0.8, blade_thickness]);
            }

            // Reinforcement ridge
            translate([0, 0, blade_thickness])
                cube([blade_length, 2, 0.8]);

            // Tang
            translate([-handle_length*0.8, (handle_width-blade_width)/2, 0])
                cube([handle_length*0.8, blade_width, blade_thickness]);
        }

        // Rivet holes
        for(i = [0:1]) {
            translate([-handle_length*0.6 + i*5, handle_width/2, -0.1])
                cylinder(h=blade_thickness+0.2, r=1.5, center=true);
        }
    }

    // Cutting teeth on bottom edge
    translate([0, 0, -tooth_height])
        saw_teeth();
}

module handle() {
    // Main handle grip
    color("saddlebrown")
    difference() {
        hull() {
            // Front section
            translate([-handle_length*0.9, 0, 0])
                cube([handle_length*0.6, handle_width, handle_thickness]);
            // Rear grip
            translate([-handle_length, 0, 0])
                sphere(d=handle_width*1.2);
        }

        // Tang slot
        translate([-handle_length*0.7, (handle_width-blade_width)/2, -0.1])
            cube([handle_length*0.6, blade_width, handle_thickness+0.2]);

        // Decorative grooves
        for(i = [0:2]) {
            translate([-handle_length*0.8 + i*3, 0, handle_thickness/2])
                cube([1, handle_width, 0.5]);
        }
    }

    // Rivets
    color("bronze")
    for(i = [0:1]) {
        translate([-handle_length*0.6 + i*5, handle_width/2, handle_thickness/2])
            cylinder(h=handle_thickness+0.5, r=1.5, center=true);
    }

    // Decorative inlay pattern
    color("gold")
    translate([-handle_length*0.3, handle_width/2-2, handle_thickness+0.1])
        cube([8, 4, 0.3]);
    translate([-handle_length*0.3, handle_width/2+2, handle_thickness+0.1])
        cube([8, 4, 0.3]);
}

