        union() {
            // Main chisel body
            cube([bar_thickness * 1.5, bar_thickness * 2.5, bar_thickness], center = true);
            // Tapered edge
            translate([0, bar_thickness * 1.25, 0])
            rotate([0, 90, 0])
            cylinder(h = bar_thickness * 1.5, r1 = bar_thickness, r2 = 0, $fn = 8);
        }
        // Edge bevel
        translate([0, bar_thickness * 1.5, 0])
        rotate([0, 0, 45])
        cube([bar_thickness * 2, bar_thickness * 2, bar_thickness * 2], center = true);
