            cylinder(h = bar_thickness * 2, r1 = bar_thickness * 1.3, r2 = bar_thickness * 1.1, $fn = 16);
        }

        // Nail slot
        translate([-curve_radius * 0.7, 0, -curve_radius * 0.7])
        cube([bar_thickness * 0.4, bar_thickness * 2, curve_radius * 1.2], center = true);
