// Wedge-shaped cuneiform stylus impression (single wedge)
module cuneus(angle=0) {
    rotate([0, 0, angle])
        translate([0, 0, 1])
        union() {
            // Main triangular wedge body (no overhangs)
            linear_extrude(height=2, convexity=2)
                polygon([[0, 0], [2.5, 0], [1.25, 2]]);
        }
}

// Row of horizontal wedges (typical sign fragment)
module wedge_row(count=5, spacing=5) {
    for(i=[0:count-1]) {
        translate([i*spacing, 0, 0])
            cuneus(0);
    }
