// Vertical wedge column
module wedge_column(count=3, spacing=4) {
    for(i=[0:count-1]) {
        translate([0, -i*spacing, 0])
            cuneus(90);
    }
}

// Full inscription field
module inscription() {
    // Front face: Sumerian administrative text
    translate([-30, 20, 13.5])
        wedge_row(6, 5);
    translate([-30, 15, 13.5])
        wedge_row(4, 5);
    translate([-30, 10, 13.5])
        wedge_column(3, 4);
    translate([-15, 10, 13.5])
        wedge_row(3, 5);
    translate([5, 20, 13.5])
        wedge_column(4, 4);
    translate([5, 5, 13.5])
        wedge_row(5, 5);

    // Right edge continuation
    translate([38, 0, 13.5])
        rotate([0, 0, 90])
            wedge_row(3, 5);

    // Back face seal impression area
    translate([-20, -20, -13.5])
        rotate([0, 180, 0])
            wedge_row(2, 6);
    translate([-10, -20, -13.5])
        rotate([0, 180, 0])
            wedge_column(2, 5);
}

