        rotate([90, 0, 0])
            cylinder(h=75, r=2, $fn=20);
    translate([37.5, 25, 25])
        rotate([90, 0, 0])
            cylinder(h=75, r=2, $fn=20);

    // Side edge rounding
    translate([-37.5, 25, 12.5])
        rotate([0, 90, 0])
            cylinder(h=50, r=2, $fn=20);
    translate([37.5, 25, 12.5])
        rotate([0, 90, 0])
            cylinder(h=50, r=2, $fn=20);
}

