// Main axle body - slightly tapered historical design
module axle_body() {
    color("BurlyWood")
    union() {
        // Central thicker section
        cylinder(h = 35, d1 = 16, d2 = 14, center = true);

        // Left taper
        translate([-17.5, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = 20, d1 = 14, d2 = 11, center = true);

        // Right taper  
        translate([17.5, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = 20, d1 = 14, d2 = 11, center = true);

        // End pegs for wheel attachment
        translate([-27, 0, 0])
            cylinder(h = 5, d = 8);
        translate([27, 0, 0])
            cylinder(h = 5, d = 8);
    }
}

// Bronze decorative collar near wheel position
module bronze_collar(position) {
    color("Goldenrod")
    translate([position, 0, 0])
    difference() {
        cylinder(h = 8, d = 20);
        cylinder(h = 10, d = 12);
    }
}

// Iron reinforcement rings - typical of Roman construction
module iron_ring(position) {
    color("SlateGray")
    translate([position, 0, 0])
    difference() {
        cylinder(h = 3, d = 18);
        cylinder(h = 4, d = 11);
    }
}

// Maker's mark inscription - simplified as raised dots
module makers_mark(position) {
    color("DarkGoldenrod")
    translate([position, 7, 0])
    for(i = [0:2]) {
        translate([i*2-2, 0, 0])
            sphere(d = 1.5);
    }
}

// Wear patterns from wheel hubs - shown as grooves
module wear_grooves(position) {
    color("SaddleBrown")
    translate([position, 0, -7])
    for(i = [0:3]) {
        rotate([0, 0, i*90])
            translate([0, 7.5, 0])
                cube([20, 1.5, 1.5], center = true);
    }
}

// Assembly of complete axle
union() {
    axle_body();

    // Bronze collars near wheel positions
    bronze_collar(-20);
    bronze_collar(20);

    // Iron reinforcement rings at stress points
    iron_ring(-10);
    iron_ring(0);
    iron_ring(10);

    // Maker's marks - typical Roman workshop identification
    makers_mark(-5);
    makers_mark(5);

    // Wear patterns from actual use
    wear_grooves(-20);
    wear_grooves(20);
}

// Medieval Astrolabe Gear - 15th Century Astronomical Instrument
// Based on historical artifacts from European observatories
// Features decorative star patterns and Latin inscriptions
// Optimized for 3D printing with 2mm wall thickness

$fn = 64;

module astrolabe_gear() {
    // Main gear body - typical brass construction
    color([0.7, 0.5, 0.2]) {
        // Central hub with reinforced core
        difference() {
            cylinder(h=6, d=20, center=true);
            cylinder(h=7, d=8, center=true);
        }

        // Gear wheel with 24 teeth (common for astrolabes - 24 hours)
        difference() {
            // Main wheel disc
            cylinder(h=4, d=80, center=true);

            // Decorative inner ring pattern
            for(i=[0:11]) {
                rotate([0, 0, i*30]) {
                    translate([25, 0, 1.5]) 
                        cube([8, 3, 2], center=true);
                }
            }

            // Central viewing window
            cylinder(h=5, d=40, center=true);
        }

        // Teeth generation using loop
        for(i=[0:23]) {
            rotate([0, 0, i*15]) {
                translate([40, 0, 0]) {
                    // Tooth base
                    cube([10, 4, 4], center=true);

                    // Rounded tooth tip
                    translate([5, 0, 0])
                        sphere(d=4);
                }
            }
        }
    }

    // Rete (star map) overlay in darker brass
    color([0.6, 0.4, 0.15]) {
        // Thin decorative overlay
        difference() {
            cylinder(h=1, d=78, center=true);

            // Star pattern holes - historical constellations
            for(star=[ [15, 20], [-20, 15], [10, -15], [-15, -10], [0, 25], [20, -5] ]) {
                translate([star[0], star[1], 0])
                    sphere(d=3);
            }

            // Latin inscription holes
            translate([0, 30, 0])
                rotate([0, 0, 0])
                    cube([25, 2, 2], center=true);
            translate([0, -30, 0])
                rotate([0, 0, 0])
                    cube([20, 2, 2], center=true);
        }

        // Radial support arms
        for(i=[0:2]) {
            rotate([0, 0, i*120]) {
                translate([0, 0, 0]) {
                    cube([2, 38, 1], center=true);
                }
            }
        }
    }

    // Alidade (sighting rule) in contrasting bronze
    color([0.5, 0.3, 0.1]) {
        // Rotatable sighting arm
        rotate([0, 0, 45]) {
            difference() {
                cube([2, 35, 2], center=true);

                // Sighting holes
                translate([0, 15, 0])
                    cylinder(h=3, d=3, center=true);
                translate([0, -15, 0])
                    cylinder(h=3, d=3, center=true);
            }
        }
    }

    // Decorative rivets at cardinal points
    color([0.8, 0.6, 0.3]) {
        for(angle=[0, 90, 180, 270]) {
            rotate([0, 0, angle]) {
                translate([30, 0, 3]) {
                    cylinder(h=1.5, d=5);
                    translate([0, 0, 0.75])
                        sphere(d=5);
                }
            }
        }
    }
}

// Scale model to 85mm diameter for printing
scale(0.85) astrolabe_gear();

// Historical Bronze Sprocket - Medieval Waterwheel Transmission Component (c. 1250-1350 AD)
// Based on finds from Cistercian abbey watermill sites in Northern France

$fn = 48;  // Smooth curves for historical accuracy

// Main parameters
sprocket_diameter = 75;  // Typical size for medieval waterwheel gearing
sprocket_thickness = 12;
hub_diameter = 20;
hub_height = 18;
tooth_count = 12;  // Common for medieval wooden chain drives
tooth_height = 8;
tooth_width = 6;
spoke_count = 4;  // Cross-shaped design typical of period

// Historical bronze patina color
color([0.4, 0.3, 0.2]) 

// Main sprocket body with historical proportions
difference() {
    // Main disk with slightly tapered edges for casting draft
    cylinder(d1 = sprocket_diameter, d2 = sprocket_diameter - 2, h = sprocket_thickness, center = true);

    // Central hole for wooden axle (slightly tapered for wedge fit)
    cylinder(d1 = hub_diameter - 8, d2 = hub_diameter - 6, h = sprocket_thickness + 2, center = true);
}

// Historical hub with decorative collar
translate([0, 0, (sprocket_thickness/2) + (hub_height/2)]) {
    color([0.35, 0.25, 0.15]) {
        // Main hub body
        cylinder(d = hub_diameter, h = hub_height);

        // Decorative collar with period-appropriate molding
        translate([0, 0, hub_height/2 - 1]) {
            cylinder(d = hub_diameter + 4, h = 2);
        }

        // Wedge slot for wooden key
        translate([0, 0, -1]) {
            cube([3, hub_diameter/2 + 2, hub_height + 2], center = true);
        }
    }
}

// Medieval cross-shaped spokes (characteristic of waterwheel gearing)
module spoke() {
    color([0.45, 0.35, 0.25]) {
        // Main spoke
        translate([0, 0, 0]) {
            cube([sprocket_diameter/2 - hub_diameter/2 - 5, sprocket_thickness - 2, sprocket_thickness], center = true);
        }

        // Period-appropriate decorative ridge
        translate([0, 0, 1]) {
            cube([sprocket_diameter/2 - hub_diameter/2 - 5, sprocket_thickness - 4, 0.5], center = true);
        }
    }
}

// Generate spokes in cross pattern
for (i = [0:45:360-45]) {
    rotate([0, 0, i]) {
        translate([hub_diameter/4 + (sprocket_diameter/4) - 2, 0, 0]) {
            spoke();
        }
    }
}

// Historical sprocket teeth with period-accurate profile
module tooth() {
    color([0.5, 0.4, 0.3]) {
        union() {
            // Main tooth body (rounded medieval profile)
            cylinder(d = tooth_width, h = tooth_height);

            // Reinforcement collar at base (casting feature)
            translate([0, 0, 0]) {
                cylinder(d1 = tooth_width + 2, d2 = tooth_width, h = 2);
            }

            // Historical wear indicator notch
            translate([tooth_width/3, 0, tooth_height - 1]) {
                cube([tooth_width/3, 1, 2]);
            }
        }
    }
}

// Generate teeth around circumference
for (i = [0:360/tooth_count:360-360/tooth_count]) {
    rotate([0, 0, i]) {
        translate([0, sprocket_diameter/2 - tooth_height/2, 0]) {
            tooth();
        }
    }
}

// Period-appropriate maker's mark inscription
translate([0, 0, sprocket_thickness/2 + 1]) {
    color([0.2, 0.15, 0.1]) {
        rotate([0, 0, 30]) {
            // "X" mark typical of medieval craftsmen
            cube([1, 8, 0.5]);
            rotate([0, 0, 45]) {
                cube([1, 6, 0.5]);
            }
            rotate([0, 0, -45]) {
                cube([1, 6, 0.5]);
            }
        }
    }
}

// Historical wear patterns (realistic casting flaws)
translate([sprocket_diameter/3, -sprocket_diameter/3, sprocket_thickness/2]) {
    color([0.3, 0.2, 0.1]) {
        sphere(d = 3);
    }
}

translate([-sprocket_diameter/4, sprocket_diameter/4, -sprocket_thickness/2]) {
    color([0.3, 0.2, 0.1]) {
        sphere(d = 2);
    }
}

// Historical ratchet mechanism from 18th century clockmaker's toolkit
// Features hand-forged iron construction with brass pawl and decorative finial

$fn = 32;

// Overall dimensions
ratchet_diameter = 60;
ratchet_thickness = 10;
pawl_length = 25;
handle_length = 40;

// Colors
color_iron = [0.25, 0.25, 0.25];
color_brass = [0.8, 0.6, 0.2];
color_steel = [0.5, 0.5, 0.5];

// Main ratchet wheel
module ratchet_wheel() {
    color(color_iron)
    difference() {
        // Basic wheel disc
        cylinder(h = ratchet_thickness, r = ratchet_diameter/2, center = true);

        // Central hole for arbor
        cylinder(h = ratchet_thickness + 2, r = 6, center = true);

        // Ornamental spokes for historical authenticity
        for(i = [0:30:150]) {
            rotate([0, 0, i])
            translate([ratchet_diameter/4, 0, 0])
            cube([ratchet_diameter/3, 6, ratchet_thickness + 2], center = true);
        }
    }

    // Teeth generation
    color(color_iron)
    for(i = [0:15:359]) {
        rotate([0, 0, i])
        translate([ratchet_diameter/2 - 2.5, 0, 0])
        difference() {
            cube([5, 8, ratchet_thickness], center = true);
            translate([0, 4, 0])
            rotate([0, 0, 45])
            cube([7, 7, ratchet_thickness + 1], center = true);
        }
    }

    // Decorative center rosette
    color(color_brass)
    translate([0, 0, ratchet_thickness/2 + 1])
    cylinder(h = 2, r = 12);

    for(i = [0:45:315]) {
        rotate([0, 0, i])
        translate([8, 0, ratchet_thickness/2 + 1])
        color(color_brass)
        cylinder(h = 2, r = 2.5);
    }
}

// Brass pawl mechanism
module pawl() {
    color(color_brass)
    union() {
        // Pawl body
        translate([ratchet_diameter/2 - 2, 0, ratchet_thickness/2 + 5])
        rotate([90, 0, 0])
        cylinder(h = pawl_length, r = 3);

        // Pawl head with catch tooth
        translate([ratchet_diameter/2 - 2, pawl_length - 2, ratchet_thickness/2 + 5])
        rotate([90, 0, 0])
        union() {
            cylinder(h = 6, r = 5);
            translate([-3, 0, 0])
            cube([6, 5, 3], center = true);
        }

        // Pawl pivot
        translate([ratchet_diameter/2 - 2, 0, ratchet_thickness/2 + 5])
        rotate([90, 0, 0])
        cylinder(h = 8, r = 1.5);
    }
}

// Hand-forged iron handle
module handle() {
    color(color_iron)
    translate([0, 0, -ratchet_thickness/2 - 15])
    rotate([0, 0, 0])
    union() {
        // Handle shaft with period-appropriate twist
        linear_extrude(height = handle_length, twist = 30)
        circle(r = 4);

        // Decorative finial
        translate([0, 0, handle_length + 4])
        union() {
            sphere(r = 6);
            translate([0, 0, -3])
            cylinder(h = 6, r = 4);
        }

        // Grip rings for hand positioning
        for(i = [10:10:30]) {
            translate([0, 0, i])
            cylinder(h = 2, r = 5);
        }
    }
}

// Assembly
rotate([0, 0, 0]) ratchet_wheel();
translate([0, 0, 0]) pawl();
handle();

// Maker's mark inscription on handle
color([0.1, 0.1, 0.1])
translate([-2, 4, 10])
rotate([90, 0, 0])
linear_extrude(height = 0.5)
text("I.S. 1783", size = 3, font = "Liberation Serif:style=Bold");

