// Medieval Iron Padlock (ca. 14th Century)
// Based on archaeological finds from York, England

$fs = 0.5;
$fa = 2;

// Overall dimensions
lock_length = 60;
lock_width = 35;
lock_height = 25;
key_hole_diameter = 8;

module body() {
    difference() {
        // Main body with rounded edges
        union() {
            color("DimGray") 
            translate([0,0,lock_height/2])
                minkowski() {
                    cube([lock_length-6, lock_width-6, lock_height-4], center=true);
                    sphere(2);
                }

            // Reinforced corners
            for(x=[-1,1])
            for(y=[-1,1])
                translate([x*(lock_length/2-3), y*(lock_width/2-3), lock_height/2])
                    cylinder(h=lock_height-4, r=3, $fn=20);
        }

        // Keyhole
        translate([lock_length/2-5, 0, lock_height/2])
            rotate([0,90,0])
                cylinder(h=15, d=key_hole_diameter, $fn=32);

        // Decorative slot
        translate([0, 0, lock_height-2])
            cube([lock_length-10, 8, 3], center=true);
    }
}

module shackle() {
    // U-shaped shackle with square cross-section
    color("DarkGray")
    union() {
        // Left post
        translate([-lock_length/2+5, 0, lock_height+15])
            rotate([0,0,0])
                cylinder(h=30, r=3, $fn=8);

        // Right post
        translate([lock_length/2-5, 0, lock_height+15])
            rotate([0,0,0])
                cylinder(h=30, r=3, $fn=8);

        // Cross bar
        translate([0, 0, lock_height+30])
            rotate([90,0,0])
                cylinder(h=lock_length-10, r=3, $fn=8);
    }
}

module decorative_plates() {
    // Ornamental brass plates on front and back
    color("Goldenrod") {
        for(side=[-1,1]) {
            translate([side*(lock_length/2-1), 0, lock_height/2])
                cube([2, lock_width-8, lock_height-8], center=true);
        }
    }
}

module rivets() {
    // Decorative rivets along edges
    color("Goldenrod") {
        for(i=[1:3]) {
            for(side=[-1,1]) {
                translate([lock_length/2-10*i, side*(lock_width/2-3), lock_height/2])
                    sphere(1.5, $fn=16);
                translate([lock_length/2-10*i, side*(lock_width/2-3), lock_height/2-8])
                    sphere(1.5, $fn=16);
            }
        }
    }
