bronze_light = [0.7, 0.5, 0.3];
patina = [0.3, 0.4, 0.35];

// Main rivet assembly
module historical_rivet() {
    // Overall bronze material
    color(bronze_dark)
    union() {
        // Domed head with slight flattening from age
        translate([0, 0, shaft_length])
        difference() {
            // Domed head
            scale([1, 1, 0.4])
            sphere(d = head_diameter);

            // Age wear - slightly flattened top
            translate([0, 0, -head_diameter/4])
            cube([head_diameter*1.2, head_diameter*1.2, head_diameter/2], center=true);
        }

        // Decorative ring below head
        translate([0, 0, shaft_length - decorative_ring_height/2])
        cylinder(h = decorative_ring_height, d1 = head_diameter*0.9, d2 = shaft_diameter*1.2);

        // Main shaft
        cylinder(h = shaft_length, d = shaft_diameter);

        // Tapered end (hammered)
        translate([0, 0, -shaft_diameter/2])
        cylinder(h = shaft_diameter/2, d1 = shaft_diameter, d2 = shaft_diameter*0.7);

        // Decorative fluting on shaft
        for(i = [0:5:355]) {
            rotate([0, 0, i])
            translate([shaft_diameter/2 + 0.5, 0, shaft_length/2])
            scale([0.3, 1, 1])
            cylinder(h = shaft_length*0.8, d = 1.5);
        }

        // Roman inscription band
        translate([0, 0, shaft_length - decorative_ring_height - inscription_height - 2])
        color(bronze_light)
        cylinder(h = inscription_height + 0.5, d = shaft_diameter*1.1);

        // Inscription details (SPQR)
        for(angle = [30, 90, 150, 210, 270, 330]) {
            rotate([0, 0, angle])
            translate([shaft_diameter/2 * 0.95, 0, shaft_length - decorative_ring_height - inscription_height/2 - 1.75])
            color([0.1, 0.1, 0.1])
            cube([0.5, 3, inscription_height*0.8], center=true);
        }
    }

    // Patina effects
    color(patina)
    translate([0, 0, shaft_length - 2])
    difference() {
        sphere(d = head_diameter * 1.05);
        sphere(d = head_diameter * 0.95);
    }

    // Additional wear patterns
    for(i = [0:45:315]) {
        rotate([0, 0, i])
        translate([head_diameter/3, 0, shaft_length + 1])
        color([0.2, 0.15, 0.1, 0.6])
        sphere(d = 2);
    }
}

