// Overall dimensions (in mm)
thimble_height = 35;
top_diameter = 15;
base_diameter = 20;
wall_thickness = 1.5;

// Create the main thimble body
module thimble_body() {
    color("brass") difference() {
        // Outer cone shape
        cylinder(h=thimble_height, d1=base_diameter, d2=top_diameter, $fn=64);

        // Hollow interior
        translate([0, 0, -0.1])
            cylinder(h=thimble_height+0.2, d1=base_diameter-wall_thickness*2, d2=top_diameter-wall_thickness*2, $fn=64);

        // Top opening
        translate([0, 0, thimble_height-0.5])
            cylinder(h=2, d=top_diameter-wall_thickness*2, $fn=32);
    }
}

// Decorative ridges around the base
module base_ridges() {
    color("brass") for(i=[1:3]) {
        translate([0, 0, i*2])
            difference() {
                cylinder(h=1, d=base_diameter+1, $fn=64);
                cylinder(h=2, d=base_diameter-1, $fn=64);
            }
    }
}

// Hand-punched dimples for needle pushing
module dimples() {
    color("brass") {
        // Spiral pattern of dimples
        dimple_diameter = 2;
        dimple_depth = 1;

        for(row=[1:6]) {
            rows_per_height = 6;
            z = row * (thimble_height-10)/rows_per_height + 5;

            circumference = PI * (base_diameter - (base_diameter-top_diameter)*(z/thimble_height));
            dimples_per_row = floor(circumference / 4);

            for(i=[0:dimples_per_row-1]) {
                angle = i * 360/dimples_per_row;
                radius = base_diameter/2 - (base_diameter-top_diameter)/2 * (z/thimble_height);

                translate([cos(angle)*radius, sin(angle)*radius, z])
                    rotate([90, 0, angle])
                        sphere(d=dimple_diameter, $fn=16);
            }
        }
    }
}

// Decorative engraved band
module engraved_band() {
    color("brass") {
        band_height = 3;
        band_z = thimble_height * 0.7;

        difference() {
            // Raised band
            cylinder(h=band_height, d1=base_diameter-2, d2=top_diameter-2, $fn=64);

            // Engraved zigzag pattern
            for(i=[0:35]) {
                angle = i * 10;
                radius = (base_diameter-2)/2 - (base_diameter-top_diameter)/2 * (band_z/thimble_height);
                x = cos(angle) * radius;
                y = sin(angle) * radius;

                translate([x, y, band_z])
                    rotate([0, 0, angle+90])
                        cube([0.5, 2, band_height], center=true);
            }
        }
    }
}

// Maker's mark on the side
module makers_mark() {
    color("brass") {
        mark_z = thimble_height * 0.3;
        mark_radius = base_diameter/2 - (base_diameter-top_diameter)/2 * (mark_z/thimble_height) - 1;

        translate([mark_radius, 0, mark_z])
            rotate([90, 0, 0])
                linear_extrude(height=0.5)
                    text("W", size=3, halign="center", valign="center");
    }
}

// Assemble the complete thimble
union() {
    thimble_body();
    base_ridges();
    dimples();
    engraved_band();
    makers_mark();
}

// Bronze Age Scriber Tool (circa 1500-1000 BCE)
