// Bronze color for the metal parts
color([0.8, 0.5, 0.2]) {
    // Main shaft
    difference() {
        union() {
            // Handle grip
            translate([0, 0, handle_length/2])
                cylinder(d = handle_diameter, h = handle_length, center = true);

            // Tapered shaft
            translate([0, 0, -tip_length/2])
                cylinder(d1 = handle_diameter * 0.7, d2 = shaft_diameter, h = tip_length + handle_length/2, center = true);

            // Decorative collar at handle end
            translate([0, 0, handle_length - 2])
                cylinder(d = handle_diameter * 1.3, h = 4, center = true);
        }

        // Grip grooves - 3 evenly spaced around handle
        for(i = [0:2]) {
            rotate([0, 0, i * 120])
                translate([handle_diameter/2 - 0.5, 0, handle_length/2])
                    cube([1, handle_diameter * 0.8, handle_length * 0.8], center = true);
        }
    }

    // Carving tip - fine point
    translate([0, 0, -(tip_length + handle_length/2 + 5)])
        cylinder(d1 = shaft_diameter, d2 = 0.5, h = 10);

    // Decorative rings on shaft
    color([0.7, 0.4, 0.15]) {
        for(i = [1:3]) {
            translate([0, 0, handle_length/2 - i * 12])
                cylinder(d = handle_diameter * 1.1, h = 1.5, center = true);
        }
    }

    // Geometric pattern band near tip
    module pattern_band() {
        difference() {
            cylinder(d = handle_diameter * 0.9, h = 6, center = true);

            // Chevron pattern cutouts
            for(i = [0:11]) {
                rotate([0, 0, i * 30])
                    translate([handle_diameter * 0.35, 0, 0])
