// Babylonian Clay Tablet (cuneiform) - 1800 BCE
// Hammurabi Code style tablet with cuneiform impressions

tablet_w = 80;
tablet_h = 55;
tablet_d = 12;

// Main tablet body
difference() {
    // Base tablet shape
    color([0.65, 0.5, 0.35]) {
        translate([-tablet_w/2, -tablet_h/2, 0])
            cube([tablet_w, tablet_h, tablet_d]);
    }

    // Rounded edges
    color([0.65, 0.5, 0.35]) {
        translate([0, 0, tablet_d])
            cylinder(h = tablet_d*2, r = tablet_w/2, $fn=32);
    }
}

// Top surface rounding
difference() {
    translate([0, 0, tablet_d-2])
        color([0.65, 0.5, 0.35]) {
            cylinder(h = 4, r = tablet_w/2-2, $fn=32);
        }

    translate([0, 0, tablet_d])
        cube([tablet_w+2, tablet_h+2, 10], center=true);
}

// Cuneiform impressions module
module cuneiform_mark(x, y, angle=0) {
    translate([x, y, tablet_d-0.3]) {
        rotate([0, 0, angle]) {
            difference() {
                cube([2, 0.5, 0.3], center=true);
                cube([1.8, 0.3, 0.4], center=true);
            }
        }
    }
}

// Header dividing line
difference() {
    translate([0, tablet_h/2-10, tablet_d-0.2])
        cube([tablet_w-10, 1, 0.2], center=true);
    for(i=[0:3]) {
        translate([-tablet_w/2+10+i*15, tablet_h/2-10, tablet_d-0.1])
            cube([2, 1.5, 0.3]);
    }
}

// Main text area cuneiform marks
for(row=[0:6]) {
    for(col=[0:8]) {
        x = -tablet_w/2+8+col*8;
        y = tablet_h/2-20-row*6;
        cuneiform_mark(x, y, (row+col)%2*30);
    }
}

// Decorative border pattern
module border_notch(x, y) {
    translate([x, y, tablet_d-0.2]) {
        difference() {
            cube([2, 2, 0.4]);
            cube([1, 1, 0.5], center=true);
        }
    }
}

// Left and right borders
for(i=[0:8]) {
    border_notch(-tablet_w/2+2, -tablet_h/2+5+i*6);
    border_notch(tablet_w/2-4, -tablet_h/2+5+i*6);
}

// Top and bottom borders
for(i=[0:11]) {
    border_notch(-tablet_w/2+5+i*6, -tablet_h/2+2);
    border_notch(-tablet_w/2+5+i*6, tablet_h/2-4);
