// Central seal impression
difference() {
    translate([0, 5, tablet_d-0.5]) {
        cylinder(h = 0.5, r = 8, $fn=32);
    }

    // Seal pattern
    for(i=[0:11]) {
        rotate([0, 0, i*30]) {
            translate([5, 0, tablet_d-0.4]) {
                rotate([90, 0, 0])
                    cylinder(h = 0.3, r1=0.5, r2=0, $fn=3);
            }
        }
    }
}

// Corner reinforcements
module corner_reinforcement(x, y) {
    translate([x, y, 0]) {
        color([0.6, 0.45, 0.3]) {
            difference() {
                cube([6, 6, tablet_d]);
                translate([3, 3, -1])
                    cylinder(h = tablet_d+2, r = 4, $fn=16);
            }
        }
    }
}

