corner_reinforcement(-tablet_w/2+3, tablet_h/2-3);
corner_reinforcement(tablet_w/2-3, tablet_h/2-3);

// Age cracks
module crack(x, y, length, angle) {
    translate([x, y, tablet_d-0.1]) {
        rotate([0, 0, angle]) {
