// Ancient Egyptian Papyrus Scroll with Hieroglyphics - Realistic Style
// Dimensions: 80mm x 50mm x 3mm when rolled

// Overall papyrus sheet
module papyrus_sheet() {
    color([0.95, 0.9, 0.75])  // Light papyrus color

    // Main rectangular sheet with slight waviness
    union() {
        // Base sheet
        difference() {
            scale([1, 1.02, 1]) cube([75, 45, 0.8], center=true);
            // Tear at bottom edge for realism
            translate([0, -22, 0]) cube([15, 4, 2], center=true);
        }

        // Thickness variation for realism
        translate([0, 0, 0.4]) scale([0.98, 0.98, 0.2]) cube([75, 45, 1], center=true);
    }
}

// Hieroglyphic symbols as raised dots
module hieroglyph(symbol_type, x, y) {
    color([0.85, 0.8, 0.65])

    translate([x, y, 0.5]) {
        if (symbol_type == 0) {
            // Eye of Horus
            sphere(d=1.5, $fn=16);
            translate([0, 0, 0.8]) sphere(d=0.8, $fn=16);
        } else if (symbol_type == 1) {
            // Ankh symbol (simplified)
            cylinder(h=1.2, d=1.2, $fn=8);
            translate([0, 1.2, 0]) rotate([90,0,0]) cylinder(h=2, d=0.6, $fn=8);
        } else if (symbol_type == 2) {
            // Scarab beetle
            sphere(d=1.8, $fn=12);
            translate([0, 0, 1]) sphere(d=1, $fn=12);
        }
    }
}

// Decorative border pattern
module border_pattern() {
    color([0.7, 0.6, 0.5])

    // Top border dots
    for(i = [-35:5:35]) {
        translate([i, 20, 0.6]) cylinder(h=0.5, d=0.8, $fn=6);
    }

    // Bottom border dots (with gap for tear)
    for(i = [-35:5:-10]) {
        translate([i, -20, 0.6]) cylinder(h=0.5, d=0.8, $fn=6);
    }
    for(i = [-5:5:35]) {
        translate([i, -20, 0.6]) cylinder(h=0.5, d=0.8, $fn=6);
    }

    // Side borders
    for(j = [-18:4:18]) {
        translate([36, j, 0.6]) cylinder(h=0.5, d=0.8, $fn=6);
        translate([-36, j, 0.6]) cylinder(h=0.5, d=0.8, $fn=6);
    }
}

// Central cartouche oval
module cartouche() {
    color([0.8, 0.75, 0.6])

    // Oval frame
    translate([0, 0, 0.7]) linear_extrude(height=0.8) {
        scale([1.3, 0.7, 1]) circle(d=25, $fn=32);
    }

    // Inner oval cutout
    translate([0, 0, 0.8]) linear_extrude(height=0.8) {
        scale([1.2, 0.6, 1]) circle(d=22, $fn=32);
    }
}

// Rope tie at one end
module rope_tie() {
    color([0.6, 0.4, 0.2])

    // Main rope
    rotate([90,0,0]) translate([30, 0, 0]) cylinder(h=3, d=2.5, $fn=12);

    // Rope wrapping details
    for(angle = [0:30:330]) {
        rotate([90, angle, 0]) translate([30, 0, 0]) 
            scale([1, 0.3, 1]) cylinder(h=3.2, d=3, $fn=6);
    }
}

// Main assembly
union() {
    // Base papyrus
    papyrus_sheet();

    // Border decoration
    border_pattern();

    // Central cartouche
    cartouche();

    // Hieroglyphics in grid pattern
    for(row = [-15:6:15]) {
        for(col = [-30:8:30]) {
            symbol = (row + col) % 3;
            hieroglyph(symbol, col, row);
        }
    }

    // Additional symbols in cartouche
    hieroglyph(0, -4, 0);
    hieroglyph(1, 0, 0);
    hieroglyph(2, 4, 0);

    // Rope tie at right edge
    rope_tie();
}

// Medieval Royal Charter Parchment
// 3D printable model with seal and decorative elements

$fn = 32;

// Main parchment dimensions
