seal_thickness = 3;

// Decorative border width
border_width = 3;

// Parchment color - aged yellow-brown
color([0.82, 0.71, 0.55]) {
    // Main parchment body
    difference() {
        // Base parchment with rounded corners
        rounded_cube(parchment_width, parchment_height, parchment_thickness, 5);

        // Holes for seal cord
        translate([parchment_width/2 - 5, parchment_height/2 - 5, -0.1])
            cylinder(h = parchment_thickness + 0.2, d = 2);
        translate([parchment_width/2 + 5, parchment_height/2 - 5, -0.1])
            cylinder(h = parchment_thickness + 0.2, d = 2);
    }

    // Decorative border pattern
    translate([0, 0, parchment_thickness])
    for(i = [0:3]) {
        rotate([0, 0, i * 90])
        translate([parchment_width/2 - border_width/2, 0, 0])
        border_pattern();
    }

    // Text lines simulation
    translate([0, 0, parchment_thickness + 0.1])
    for(y = [-20:10:20]) {
        translate([0, y, 0])
        text_line(60, 0.5);
    }

    // Wax seal attached with cord
    translate([parchment_width/2 - 5, parchment_height/2 + 15, -seal_thickness/2])
    rotate([90, 0, 0])
    color([0.6, 0.2, 0.2]) {
        // Seal body
        cylinder(h = seal_thickness, d = seal_diameter);

        // Royal crown impression on seal
        translate([0, 0, seal_thickness + 0.1])
        scale([0.8, 0.8, 1])
        royal_crown();
    }

    // Hanging cord from seal
    translate([parchment_width/2 - 5, parchment_height/2 + 15, 0])
    rotate([0, 90, 0])
    color([0.5, 0.3, 0.1])
    cord();
}

// Module for rounded cube
module rounded_cube(w, h, t, r) {
    offset = r * (1 - sqrt(2)/2);
    linear_extrude(height = t)
    offset(r = r)
    offset(r = -r)
    square([w - 2*r, h - 2*r], center = true);
}

// Module for decorative border elements
module border_pattern() {
    for(i = [-1:1]) {
        translate([0, i * (parchment_height/2 - border_width), 0])
        difference() {
            cube([border_width, 6, 1.2], center = true);
            cylinder(h = 2, d = 3, center = true);
        }
    }
}

// Module for text lines
module text_line(length, height) {
    cube([length, height, 0.3], center = true);
}

// Module for royal crown impression
module royal_crown() {
    union() {
        // Crown base
        cylinder(h = 2, d = seal_diameter * 0.7);

        // Crown points
        for(i = [0:4]) {
            rotate([0, 0, i * 72])
            translate([seal_diameter * 0.25, 0, 1])
            cylinder(h = 3, d1 = 3, d2 = 1);
        }

        // Central jewel
        translate([0, 0, 3])
        sphere(d = 2);
    }
}

// Module for hanging cord
module cord() {
    rotate_extrude(angle = 180, $fn = 16)
    translate([2, 0, 0])
    circle(d = 1.5);
}

// Medieval illuminated manuscript on vellum - realistic style
// Based on 13th century Book of Hours

// Main parameters
manuscript_width = 80;
manuscript_height = 60;
manuscript_thickness = 8;
page_thickness = 0.5;
cover_thickness = 1.5;

// Colors
vellum_color = [0.95, 0.92, 0.82]; // Warm cream vellum
leather_color = [0.35, 0.20, 0.10]; // Dark brown leather
gold_color = [0.83, 0.69, 0.22]; // Antique gold
ink_color = [0.15, 0.15, 0.15]; // Iron gall ink

// Main manuscript body
module manuscript_body() {
    difference() {
        // Stack of vellum pages
        color(vellum_color) {
            for(i = [-manuscript_thickness/2 + cover_thickness : page_thickness : manuscript_thickness/2 - cover_thickness]) {
                translate([0, 0, i]) {
                    cube([manuscript_width, manuscript_height, page_thickness * 0.8], center=true);
                }
            }
        }

        // Hole for binding cord
        translate([manuscript_width/2 - 5, 0, 0]) {
            rotate([90, 0, 0]) {
                cylinder(h=manuscript_height + 2, r=1.5, center=true);
            }
        }
    }
}

// Leather cover with tooling
module leather_cover() {
    color(leather_color) {
        // Front cover
        translate([0, 0, -manuscript_thickness/2 + cover_thickness/2]) {
            cube([manuscript_width + 2, manuscript_height + 2, cover_thickness], center=true);
        }

        // Back cover
        translate([0, 0, manuscript_thickness/2 - cover_thickness/2]) {
            cube([manuscript_width + 2, manuscript_height + 2, cover_thickness], center=true);
        }

        // Spine
        translate([manuscript_width/2 + 1, 0, 0]) {
            rotate([0, 90, 0]) {
                cube([manuscript_thickness, manuscript_height + 2, cover_thickness], center=true);
            }
        }
    }
}

// Decorative tooling pattern on cover
module cover_tooling() {
    color([0.25, 0.15, 0.08]) {
        // Central medallion
        translate([0, 0, -manuscript_thickness/2 + cover_thickness + 0.1]) {
            cylinder(h=0.2, r=12, center=false);
        }

        // Corner decorations
        for(x = [-manuscript_width/2 + 15, manuscript_width/2 - 15]) {
            for(y = [-manuscript_height/2 + 15, manuscript_height/2 - 15]) {
                translate([x, y, -manuscript_thickness/2 + cover_thickness + 0.1]) {
                    rotate([0, 0, 45]) {
                        cube([8, 8, 0.2], center=true);
                        for(i = [-3, 3]) {
                            translate([i, 0, 0]) {
                                cylinder(h=0.2, r=1, center=false);
                            }
                            translate([0, i, 0]) {
                                cylinder(h=0.2, r=1, center=false);
                            }
                        }
                    }
                }
            }
        }
    }
}

// Illuminated capital letter
module illuminated_capital() {
    color(gold_color) {
        // Gold leaf background
        translate([-manuscript_width/2 + 10, manuscript_height/2 - 15, -manuscript_thickness/2 + cover_thickness + page_thickness + 0.2]) {
            cube([12, 12, 0.3], center=false);
        }
    }

    color(ink_color) {
        // Initial letter "B"
        translate([-manuscript_width/2 + 14, manuscript_height/2 - 12, -manuscript_thickness/2 + cover_thickness + page_thickness + 0.5]) {
            // Vertical stroke
            cube([2, 10, 0.2], center=false);

            // Top curve
            translate([2, 7, 0]) {
                cylinder(h=0.2, r=3, center=false);
            }

            // Middle bar
            translate([2, 3, 0]) {
                cube([4, 2, 0.2], center=false);
            }

            // Bottom curve
            translate([2, 0, 0]) {
                cylinder(h=0.2, r=3, center=false);
            }
        }
    }
}

// Text lines on vellum
module manuscript_text() {
    color(ink_color) {
        for(i = [1:7]) {
            // Main text lines
            translate([-manuscript_width/2 + 15, manuscript_height/2 - 20 - i * 6, -manuscript_thickness/2 + cover_thickness + page_thickness + 0.2]) {
                cube([manuscript_width - 30, 0.5, 0.1], center=false);
            }
        }

        // Decorative red rubrication
        color([0.65, 0.15, 0.15]) {
            translate([-manuscript_width/2 + 15, manuscript_height/2 - 27, -manuscript_thickness/2 + cover_thickness + page_thickness + 0.2]) {
                cube([8, 0.8, 0.1], center=false);
            }
        }
    }
}

// Brass clasp for closing
module brass_clasp() {
    color(gold_color) {
        // Front clasp
        translate([manuscript_width/2 + 1, manuscript_height/2 - 10, -manuscript_thickness/2 + cover_thickness/2]) {
            cube([8, 3, cover_thickness], center=false);
        }

        // Back clasp receiver
        translate([manuscript_width/2 + 1, manuscript_height/2 - 10, manuscript_thickness/2 - cover_thickness/2]) {
            cube([8, 3, cover_thickness], center=false);
        }

        // Hinge detail
        for(i = [-manuscript_height/2 + 10 : 10 : manuscript_height/2 - 10]) {
            translate([manuscript_width/2 + 1, i, 0]) {
                rotate([0, 90, 0]) {
                    cylinder(h=3, r=1, center=true);
                }
            }
        }
    }
}

