// Medieval Wax-Sealed Deed Scroll
// 14th century European property deed

use <colors.scad>  // fallback: render with natural colors

// Overall dimensions for 3D printing
scroll_length = 75;
scroll_diameter = 12;
seal_diameter = 18;
seal_thickness = 3;

module scroll_body() {
    // parchment rolled into loose cylinder
    difference() {
        // main roll
        color("antique_white")
        cylinder(h = scroll_length, d = scroll_diameter, $fn = 32);

        // hollow core so it prints without supports
        translate([0, 0, -0.1])
            cylinder(h = scroll_length + 0.2, d = scroll_diameter - 2, $fn = 32);
    }

    // slightly unrolled outer flap
    translate([scroll_diameter/2, 0, scroll_length/2])
        rotate([0, 85, 0])
        color("antique_white")
        cube([scroll_length/3, scroll_diameter, 0.8], center = true);
}

module seal() {
    // disk of red sealing wax
    color("firebrick")
    translate([0, 0, scroll_length - seal_thickness/2])
    cylinder(h = seal_thickness, d = seal_diameter, $fn = 64);

    // simple heraldic emblem raised relief
    translate([0, 0, scroll_length + 0.2])
    color("darkred")
    linear_extrude(height = 0.5)
    union() {
        circle(d = 12, $fn = 32);
        // cross
        square([1, 12], center = true);
        square([12, 1], center = true);
    }
}

module ribbon() {
    // twin cords holding seal
    for (offset = [-2.5, 2.5]) {
        color("sienna")
        translate([offset, 0, scroll_length - 3])
        cube([1.5, scroll_diameter, 2.5]);
    }
}

module inscription_lines() {
    // shallow engraved text lines on parchment
    for (y = [-scroll_diameter/2 + 3 : 4 : scroll_diameter/2 - 3]) {
        translate([0, y, scroll_length/2])
        color("wheat")
        cube([scroll_length - 10, 0.4, 0.2], center = true);
    }
}

module decorative_borders() {
    // repeating leaf motifs at top and bottom edges
    module leaf() {
        // simple stylized leaf
        rotate([-90, 0, 0])
        color("goldenrod")
        translate([0, 0, scroll_diameter/2 + 0.5])
        cylinder(h = 1.2, d1 = 2.5, d2 = 1.2, $fn = 6);
    }

    for (i = [0 : 6 : scroll_length - 6]) {
        translate([i - scroll_length/2 + 3, 0, 0])
            leaf();
        translate([i - scroll_length/2 + 3, 0, scroll_length])
            rotate([0, 180, 0])
            leaf();
    }
}

// Assemble the deed
union() {
    scroll_body();
    seal();
    ribbon();
    inscription_lines();
    decorative_borders();
}

