    color("gold") {
        translate([book_width/2 - 3, 0, book_thickness/2 + 1]) {
            cube([2, 8, 1], center=true);
            translate([0, 4, 0]) cylinder(h=1, r=1.5, $fn=16);
        }
        mirror([1,0,0]) translate([book_width/2 - 3, 0, book_thickness/2 + 1]) {
            cube([2, 8, 1], center=true);
            translate([0, 4, 0]) cylinder(h=1, r=1.5, $fn=16);
        }
    }
}

module decorative_corners() {
    // Decorative brass corner protectors
    color("gold") {
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * book_width/2, y * book_height/2, 0]) {
                    difference() {
                        cube([4, 4, cover_thickness + 0.5], center=true);
                        cylinder(h=cover_thickness + 1, r=2.5, center=true, $fn=8);
                    }
                }
            }
        }
    }
}

module spine_details() {
    // Raised bands on spine
    color("saddlebrown") {
        for(i = [-1, 0, 1]) {
            translate([0, i * 12, book_thickness/2 - 1]) {
                difference() {
                    cube([book_thickness + 2, 6, 3], center=true);
                    sphere(r=book_thickness/2 + 1);
                }
            }
        }
    }
}

module pages() {
