    color("ivory") {
        translate([0, 0, -page_thickness/2 + 0.5]) {
            difference() {
                cube([book_width - 2, book_height - 2, page_thickness], center=true);
                translate([0, 0, -1]) cube([book_width - 4, book_height - 4, page_thickness + 2], center=true);
            }
        }
    }
}

module cover() {
