    color("saddlebrown") {
        translate([0, 0, 0]) {
            difference() {
                // Front cover
                cube([book_width, book_height, cover_thickness], center=true);
                // Inner cavity
                translate([0, 0, -0.5]) cube([book_width - 2, book_height - 2, cover_thickness + 1], center=true);
