cover_thickness = 2;

// Colors
wood_color = [139/255, 69/255, 19/255];
leather_color = [101/255, 67/255, 33/255];
page_color = [245/255, 222/255, 179/255];
gold_color = [212/255, 175/255, 55/255];

module book_cover() {
    difference() {
        // Main wooden board
        color(wood_color)
        cube([book_width, book_height, cover_thickness], center=true);

        // Recessed panel
        translate([0, 0, -0.5])
        cube([book_width-6, book_height-6, cover_thickness], center=true);
    }
}

module metal_corner(x, y) {
    translate([x, y, 0])
    color(gold_color)
    difference() {
        cube([6, 6, cover_thickness+0.5], center=true);
        cylinder(h=cover_thickness+1, r=1.5, center=true);
    }
}

module clasp() {
    translate([book_width/2-5, 0, 0])
    rotate([90, 0, 0])
    color(gold_color)
    difference() {
        cube([8, 4, 2], center=true);
        translate([0, -2.5, 0])
        cylinder(h=3, r=1.5, center=true);
    }
}

module pages() {
    difference() {
        // Main page block
        color(page_color)
        for(i = [-page_thickness*2:page_thickness/2:page_thickness*2]) {
            translate([i/2, 0, 0])
            cube([book_width-cover_thickness*2, book_height-cover_thickness*2, book_thickness-cover_thickness*2], center=true);
        }

        // Spine curve
        translate([-book_width/2+cover_thickness, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=book_height-cover_thickness*2, r=book_thickness/4, center=true);
    }
}

module decorative_rivets() {
    // Rivets along spine
    for(i = [-book_height/2+5:10:book_height/2-5]) {
        translate([-book_width/2-cover_thickness/2, i, 0])
        color(gold_color)
        sphere(r=1.5);
    }
}

module spine_bands() {
    // Raised bands on spine
    for(i = [-book_height/3, 0, book_height/3]) {
        translate([-book_width/2-cover_thickness/2, i, 0])
        color(leather_color)
        cube([cover_thickness, 3, book_thickness], center=true);
    }
}

// Main assembly
union() {
    // Front cover
    translate([0, 0, book_thickness/2])
    book_cover();

    // Back cover
    translate([0, 0, -book_thickness/2])
    book_cover();

    // Pages
    pages();

    // Metal corners
    metal_corner(book_width/2-3, book_height/2-3);
    metal_corner(book_width/2-3, -book_height/2+3);
    metal_corner(-book_width/2+3, book_height/2-3);
    metal_corner(-book_width/2+3, -book_height/2+3);

    // Clasps
    translate([0, book_height/2-5, book_thickness/2])
    clasp();
    translate([0, -book_height/2+5, -book_thickness/2])
    rotate([0, 180, 0])
    clasp();

    // Spine details
    decorative_rivets();
    spine_bands();

    // Leather covering
    color(leather_color)
    translate([0, 0, book_thickness/2+cover_thickness/2])
    cube([book_width+1, book_height+1, 0.5], center=true);
    translate([0, 0, -book_thickness/2-cover_thickness/2])
    cube([book_width+1, book_height+1, 0.5], center=true);
}

