// Medieval Epistolary (Letter Case) - 15th Century Style
// Typical size for holding wax-sealed letters and documents

$fa = 2;
$fs = 0.5;

// Main dimensions
case_width = 80;
case_height = 60;
case_depth = 25;
wall_thickness = 2.5;
hinge_radius = 3;

// Colors
wood_color = "#8B4513";
leather_color = "#3C2414";
metal_color = "#B87333";

// Wood grain texture module
module wood_texture(width, height, depth) {
    for (i = [0:2:width]) {
        translate([i, 0, -0.2])
            cube([0.5, height, 0.4]);
    }
}

// Decorative metal corner
module metal_corner() {
    color(metal_color)
    union() {
        // Main corner plate
        translate([0, 0, 0])
            cube([8, 8, 1.5]);

        // Decorative rivets
        for (x = [1.5, 6.5])
            for (y = [1.5, 6.5])
                translate([x, y, 1.5])
                    cylinder(h=1, r=0.8, $fn=8);
    }
}

// Hinge mechanism
module hinge() {
    color(metal_color)
    union() {
        // Hinge barrel
        cylinder(h=case_width, r=hinge_radius, $fn=16);

        // Hinge pins
        for (i = [0:10:case_width-10])
            translate([0, 0, i])
                cylinder(h=3, r=hinge_radius+0.5, $fn=8);
    }
}

// Main case body
module case_body() {
    color(wood_color)
    difference() {
        // Outer shell
        cube([case_width, case_height, case_depth]);

        // Inner cavity
        translate([wall_thickness, wall_thickness, wall_thickness/2])
            cube([case_width-wall_thickness*2, case_height-wall_thickness*2, case_depth-wall_thickness]);
    }

    // Wood texture on top
    translate([0, 0, case_depth])
        wood_texture(case_width, case_height, 0.5);
}

// Leather strap with buckle
module leather_strap() {
    color(leather_color)
    union() {
        // Main strap
        translate([case_width+5, case_height/2-5, 0])
            cube([20, 10, 2]);

        // Buckle
        color(metal_color)
        translate([case_width+20, case_height/2-5, 1])
            difference() {
                cube([8, 10, 2]);
                translate([4, 5, -0.5])
                    cylinder(h=3, r=2, $fn=16);
            }
    }
}

// Wax seal impression on front
module wax_seal() {
    color("#8B0000")
    translate([case_width/2-10, case_height/2-10, case_depth+0.5])
    union() {
        cylinder(h=3, r=10, $fn=32);
        translate([0, 0, 3])
            cylinder(h=1, r=8, $fn=32);
    }
}

// Assembly
union() {
    // Bottom half
    translate([0, 0, 0])
        case_body();

    // Top half (lid)
    translate([0, 0, case_depth])
        rotate([180, 0, 0])
            case_body();

    // Hinge connecting halves
    translate([case_width/2, -hinge_radius, case_depth])
        rotate([90, 0, 0])
            hinge();

    // Metal corners
    for (x = [0, case_width-8])
        for (y = [0, case_height-8])
            translate([x, y, case_depth-1.5])
                metal_corner();

    // Leather strap
    translate([0, 0, case_depth])
        leather_strap();

    // Wax seal decoration
    wax_seal();
}

