rivet_height = 0.5;
rivet_count = 4;

// Color definitions
wood_color = [139/255, 90/255, 43/255];
metal_color = [192/255, 192/255, 192/255];
page_color = [245/255, 245/255, 220/255];

module metal_corner(x_pos, y_pos, z_pos) {
    color(metal_color)
    translate([x_pos, y_pos, z_pos])
    union() {
        // Main corner piece
        difference() {
            cube([corner_size, corner_size, corner_thickness]);
            // Rounded inner corner
            translate([corner_size-2, corner_size-2, -1])
                cylinder(h=corner_thickness+2, r=2);
        }

        // Decorative rivets
        for(i=[-1, 1]) {
            for(j=[-1, 1]) {
                translate([corner_size/2 + i*2.5, corner_size/2 + j*2.5, corner_thickness])
                    cylinder(h=rivet_height, r=rivet_radius);
            }
        }
    }
}

module spine_ribs() {
    color(metal_color)
    for(i=[0:spine_rib_count-1]) {
        translate([-book_thickness/2 - 0.1, 
                  -book_height/2 + (book_height/(spine_rib_count-1))*i, 
                  0])
            cube([spine_rib_height, spine_rib_width, book_thickness + page_thickness]);
    }
}

module central_boss() {
    color(metal_color)
    difference() {
        // Main boss
        translate([0, 0, cover_thickness])
            cylinder(h=boss_height, r=boss_radius);

        // Decorative ring
        translate([0, 0, cover_thickness + boss_height/2])
            cylinder(h=boss_height + 0.1, r=boss_radius - 2);

        // Center rivet
        translate([0, 0, cover_thickness + boss_height - 0.1])
            cylinder(h=rivet_height + 0.2, r=rivet_radius + 0.5);
    }
}

