// Medieval Passional Book Box
// A small devotional book box used in the 14th-15th century for storing prayers and relics

// Overall dimensions (in mm)
book_length = 80;
book_width = 60;
book_thickness = 25;
page_thickness = 2;
cover_thickness = 3;

// Book cover
module book_cover() {
    // Main cover body
    color([0.6, 0.3, 0.1])  // Dark brown leather color
    difference() {
        cube([book_length, book_width, cover_thickness], center=true);

        // Inner cavity for pages
        translate([0, 0, -cover_thickness/2 + 0.5])
        cube([book_length-6, book_width-6, cover_thickness], center=true);
    }

    // Raised bands on spine (simulating leather cords)
    for(i = [-20:10:20]) {
        translate([book_length/2 - 3, i, 0])
        color([0.5, 0.25, 0.08])
        cube([2, 6, cover_thickness+0.5]);
    }

    // Decorative corner pieces
    for(x = [-book_length/2+5:book_length-10:book_length-10]) {
        for(y = [-book_width/2+5:book_width-10:book_width-10]) {
            translate([x, y, cover_thickness/2])
            color([0.8, 0.6, 0.2])  // Brass color
            cylinder(h=1, r=3, $fn=32);
        }
    }
}

// Metal clasp
module clasp() {
    color([0.7, 0.7, 0.7])  // Silver color
    translate([book_length/2-5, 0, cover_thickness/2+1])
    rotate([90, 0, 0])
    difference() {
        cylinder(h=15, r=2, $fn=16);
        translate([0, 0, -1])
        cube([4, 2, 17], center=true);
    }

    // Catch plate
    translate([-book_length/2+3, 0, cover_thickness/2])
    color([0.8, 0.6, 0.2])
    cube([6, 8, 1]);
}

// Decorative cross on cover
module cover_cross() {
    color([0.8, 0.6, 0.2])  // Brass color
    translate([0, 0, cover_thickness/2+0.5])
    union() {
        cube([25, 5, 1]);
        cube([5, 35, 1]);
    }

    // Small decorative studs
    for(angle = [0:90:270]) {
        rotate([0, 0, angle])
        translate([10, 10, cover_thickness/2+1])
        sphere(r=1.5);
    }
}

// Pages inside
module pages() {
    color([0.95, 0.9, 0.7])  // Aged parchment color
    translate([0, 0, 0])
    difference() {
        cube([book_length-10, book_width-10, page_thickness*4], center=true);

        // Rounded page edges
        translate([0, 0, -page_thickness*2-1])
        cylinder(h=page_thickness*4+2, r=(book_width-10)/2-2, $fn=64);
    }
}

// Assembly
module passional_book() {
    // Bottom cover
    translate([0, 0, -page_thickness/2-cover_thickness/2])
    book_cover();

    // Top cover
    translate([0, 0, page_thickness/2+cover_thickness/2])
    book_cover();

    // Pages
    pages();

    // Clasp on front edge
    clasp();

    // Decorative cross on top cover
    translate([0, 0, page_thickness+cover_thickness])
    cover_cross();

    // Small ring for carrying
    color([0.8, 0.6, 0.2])
    translate([-book_length/2-3, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=5, r=2, $fn=16);
}

// Render the complete passional
passional_book();

