        difference() {
            cylinder(h=scroll_length, d=scroll_diameter, center=true, $fn=64);
            cylinder(h=scroll_length+2, d=scroll_diameter-4, center=true, $fn=64);
        }

        // Rolled edge detail
        for(i=[-1:0.5:1]) {
            rotate([90, 0, 0])
                translate([0, scroll_length/2 + i*2, scroll_diameter/2 - 1])
                    cube([scroll_diameter-2, 0.5, 2], center=true);
