            rotate([90, 0, 0])
                cylinder(h=3, d=seal_diameter, $fn=32);

        // Seal impression (constitution symbol - scales of justice)
        translate([scroll_diameter/2 + 2.5, 0, 0])
            rotate([90, 0, 0]) {
