// Historical Proclamation Tablet - Roman Style
// 3D printable replica with imperial seal and text areas

// Overall dimensions
tablet_width = 70;
tablet_height = 90;
tablet_thickness = 6;
border_width = 4;

// Colors
tablet_color = "burlywood";
seal_color = "goldenrod";
text_color = "saddlebrown";

// Main tablet body with rounded edges
module tablet_body() {
    color(tablet_color) {
        // Main rectangular body
        difference() {
            translate([0, 0, tablet_thickness/2])
                cube([tablet_width, tablet_height, tablet_thickness], center=true);

            // Rounded edges on back
            translate([0, 0, -tablet_thickness/2 + 0.5])
                cube([tablet_width - 4, tablet_height - 4, 2], center=true);
        }

        // Top and bottom decorative borders
        translate([0, tablet_height/2 - border_width/2, tablet_thickness/2])
            cube([tablet_width, border_width, tablet_thickness], center=true);
        translate([0, -tablet_height/2 + border_width/2, tablet_thickness/2])
            cube([tablet_width, border_width, tablet_thickness], center=true);
    }
}

// Imperial seal decoration
module imperial_seal() {
    color(seal_color) {
        // Central seal disc
        translate([0, 20, tablet_thickness + 0.1])
            cylinder(h=2, r=12, $fn=32);

        // Eagle motif (simplified)
        translate([0, 20, tablet_thickness + 2.2])
            scale([1, 0.7, 1])
                sphere(r=8, $fn=24);

        // Laurel wreath around seal
        for(i = [0:30:330]) {
            rotate([0, 0, i])
                translate([10, 20, tablet_thickness + 1.5])
                    rotate([90, 0, 0])
                        cylinder(h=0.5, r1=3, r2=1, $fn=8);
        }
    }
}

// Text inscription areas
module text_blocks() {
    color(text_color) {
        // Main proclamation text area
        translate([0, -15, tablet_thickness + 0.1])
            cube([50, 25, 0.5], center=true);

        // Title area
        translate([0, 35, tablet_thickness + 0.1])
            cube([40, 8, 0.5], center=true);

        // Signature line
        translate([20, -35, tablet_thickness + 0.1])
            cube([25, 3, 0.5], center=true);
    }
}

// Decorative corner elements
module corner_decorations() {
    color(seal_color) {
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                translate([x * (tablet_width/2 - 8), y * (tablet_height/2 - 8), tablet_thickness/2])
                    cylinder(h=tablet_thickness, r=4, $fn=8);

                translate([x * (tablet_width/2 - 8), y * (tablet_height/2 - 8), tablet_thickness + 0.1])
                    sphere(r=2, $fn=8);
            }
        }
    }
}

// Holes for hanging/display
module mounting_holes() {
    translate([0, tablet_height/2 - 10, 0])
        rotate([90, 0, 0])
            cylinder(h=tablet_width, r=2, center=true);
}

// Assembly
union() {
    tablet_body();
    imperial_seal();
    text_blocks();
    corner_decorations();

    // Add mounting hole (remove material)
    difference() {
        children();
        mounting_holes();
    }
}

