// Babylonian cuneiform tablet (ca. 2000 BCE)
// Overall size: 75 mm x 50 mm x 20 mm

$fn = 50;
wall = 1.5;

module tablet_body() {
    difference() {
        translate([0, 0, 10]) {
            color([0.65, 0.48, 0.35])  // fired clay color
            cube([75, 50, 20], center = true);
        }
        // Hollow interior
        translate([0, 0, 11]) {
            cube([72, 47, 18], center = true);
        }
    }
}

module cuneiform_line(start, end, depth) {
    // Simple wedge-shaped impressions
    num_wedges = ceil(norm(end - start) / 4);
    for (i = [0 : num_wedges - 1]) {
        pos = start + (end - start) * i / (num_wedges - 1);
        translate(pos + [0, 0, 10 + depth]) {
            rotate([0, 0, atan2(end.y - start.y, end.x - start.x)])
            difference() {
                cube([3, 2, depth + 0.5], center = true);
                translate([0, 0, 0.5])
                cube([2.5, 1.5, depth + 1], center = true);
            }
        }
    }
}

module cuneiform_text() {
    color([0.35, 0.25, 0.15]) {
        // Front face text - typical Babylonian accounting tablet
        // Row 1: 120 sheep
        cuneiform_line([-30, 15, 0], [30, 15, 0], 1.5);
        cuneiform_line([-30, 10, 0], [-20, 10, 0], 1.5);

        // Row 2: 60 goats
        cuneiform_line([-30, 5, 0], [30, 5, 0], 1.5);
        cuneiform_line([-30, 0, 0], [-10, 0, 0], 1.5);

        // Row 3: 30 cattle
        cuneiform_line([-30, -5, 0], [30, -5, 0], 1.5);
        cuneiform_line([-30, -10, 0], [0, -10, 0], 1.5);

        // Seal impression - administrative cylinder seal pattern
        translate([0, -15, 11.5]) {
            rotate([90, 0, 0])
            difference() {
                cylinder(h = 3, r = 8);
                union() {
                    for (i = [0:7]) {
                        rotate([0, 0, 45 * i])
                        translate([3, 0, -1])
                        cube([1.5, 1, 5], center = true);
                    }
                    translate([0, 0, 0])
                    cylinder(h = 3, r = 2);
                }
            }
        }
    }
}

module edge_chips() {
    // Historical wear and damage
    color([0.6, 0.45, 0.3]) {
        // Top edge chips
        translate([-35, 20, 20]) {
            sphere(r = 3);
        }
        translate([30, 18, 20]) {
            sphere(r = 2.5);
        }

        // Corner damage
        translate([-37.5, -25, 10]) {
            sphere(r = 4);
        }
        translate([37.5, 25, 8]) {
            sphere(r = 3.5);
        }
    }
}

module back_surface() {
    color([0.65, 0.48, 0.35]) {
        // Back of tablet - continuation of text
        translate([0, 0, -10]) {
            difference() {
                cube([75, 50, 0.5], center = true);
                // Summary on back
                translate([0, 10, 0])
                cube([40, 2, 2], center = true);
                translate([0, 5, 0])
                cube([30, 2, 2], center = true);
                translate([0, 0, 0])
                cube([50, 2, 2], center = true);
            }
        }
    }
}

